int dp[10][10][10];

int calc(int l, int r, int k)
{
	if (l == r) return (a[l] + k) % 10 == b[l] ? 0 : 1 << 28;
	int &res = dp[l][r][k];
	if (~res) return res;
	res = 1 << 28;
	for (int m = l; m < r; m++){
		for (int i = 0; i < 10; i++){
			for (int j = 0; j < 10; j++){
				int lk = (k + i) % 10;
				int rk = (k + j) % 10;
				int cs = !!i + !!j;
				res = min(res, cs + calc(l, m, lk) + calc(m + 1, r, rk));
			}
		}
	}
	return res;
}

