  std::vector<int> ini_vec(k);
  std::vector<int> unl_vec(k);
  for(int i=0;i<k;i++){
    ini_vec[i]=ini[i]-'0';
    unl_vec[i]=unl[i]-'0';
  }
  stk.push(State(ini_vec,0));
  int result=INT32_MAX;
