class Str
{
public:
	string s;
	int c;
	Str(string s, int c)
	:s(s),c(c)
	{}

	bool operator<(const Str& sr) const
	{
		if(s!=sr.s) return s<sr.s;
		return c<sr.c;
	}
};

int K,lim;
string sor, dis;
set<Str> st;

string rot(int p, int n, string d)
{
	string ret=d;

	int t=(ret[p]-'0')+n;
	if(t<0) t+=10;
	ret[p]=(t%10)+'0';

	return ret;
}

int dist(string str)
{
	bool dig[10]={0}, dig2[10]={0};
	for(int i=0; i<K; i++)
	{
		if(str[i]==dis[i]) continue;
		int l=str[i]-'0', r=dis[i]-'0';
		dig2[abs(l-r)]=1;
		if(l==0) l=10; 
		if(r==0) r=10;
		dig[abs(l-r)]=1;
	}

	int ret=0, ret2=0;
	for(int i=0; i<10; i++)
	{
		if(dig[i]) ret++;
		if(dig2[i]) ret2++;
	}

	return min(ret, ret2);
}

bool dfs(string rt, int c, int pd)
{
	if(rt==dis) return true;
	if(dist(rt)+c>lim) return false;

	for(int i=0; i<K; i++)
	for(int k=-5; k<=5; k++)
	{
		if(k==0) continue;
		string t=rt;
		for(int j=i; j<K; j++)
		{
			t=rot(j,k,t);
			if(dist(t)+c > pd) continue;
			if(!st.insert(Str(t,c)).second) continue;

			if(dfs(t,c+1, min(pd, dist(t)+c))) return true; 
		}
	}

	return false;
}
