typedef pair<int, string> State; 

bool allZero(const string &s) {
  for(int i = 0; i < s.size(); ++i) {
    if(s[i] != 0) return false;
  }
  return true;
}

string change(string str, int s, int e, char a) {
  for(int i = s; i < e; ++i) if(str[i] == a) str[i] = 0;
  return str;
}

int solve(string start) {
  State s(0, start);
  queue<State> que;
  que.push(s);
  while(!que.empty()) {
    s = que.front(); que.pop();
    /*
    for(int i = 0; i < s.second.size(); ++i) cout << (int)s.second[i];
    cout << endl;
    */
    string &str = s.second;
    if(allZero(s.second)) return s.first;
    int l, r, i, j;
    for(l = 0; l < str.size() && str[l] == 0; ++l);
    for(r = (int)str.size()-1; r >= 0 && str[r] == 0; --r);
    ++r;
    for(i = l+1; i < str.size() && str[i] == str[l]; ++i);
    que.push(State(s.first+1, change(str, l, i, str[l])));
    for(j = r-1; j >= 0 && str[j] == str[r-1]; --j);
    ++j;
    que.push(State(s.first+1, change(str, j, r, str[j])));
    if(str[l] == str[j]) que.push(State(s.first+1, change(str, l, r, str[l])));
  }
  return -1;
}

