public class Main {
	InputStream is;

	boolean __FILE_DEBUG_FLAG__ = true;
	String __DEBUG_FILE_NAME__ = "T";

	FastScanner in;
	PrintWriter out;

	int rec(int[] d, int pos) {
		if (pos == d.length) return 0;
		if (d[pos] == 0) return rec(d, pos + 1);

		int prev = d[pos];
		int res = Integer.MAX_VALUE;
		for (int i = pos; i < d.length; i++) {
			d[i] = (10 + d[i] - prev) % 10;
			res = Math.min(res, rec(d, pos + 1) + 1);
		}

		for (int i = pos; i < d.length; i++) {
			d[i] = (d[i] + prev) % 10;
		}
		return res;
	}

	public void solve() {	
		while (true) {
			int n = in.nextInt();
			if (n == 0) break;
			String s1 = in.next(), s2 = in.next();

			int[] d = new int[n];
			for (int i = 0; i < n; i++) {
				d[i] = (10 + s2.charAt(i) - s1.charAt(i)) % 10;
			}
			System.out.println(rec(d, 0));
		}
	}

	public void run() {
		if (__FILE_DEBUG_FLAG__) {
			try {
				is = new FileInputStream(__DEBUG_FILE_NAME__);
			} catch (FileNotFoundException e) {
				e.printStackTrace();
			}
		} else {
			is = System.in;
		}
		in = new FastScanner(is);
		out = new PrintWriter(System.out);

		solve();
	}

	public static void main(String[] args) {
		new Main().run();
	}

