	class FastScanner {
		private InputStream stream;
		private byte[] buf = new byte[1024];
		private int curChar;
		private int numChars;

		public FastScanner(InputStream stream) {
			this.stream = stream;
			//stream = new FileInputStream(new File("dec.in"));

		}

		int read() {
			if (numChars == -1)
				throw new InputMismatchException();
			if (curChar >= numChars) {
				curChar = 0;
				try {
					numChars = stream.read(buf);
				} catch (IOException e) {
					throw new InputMismatchException();
				}
				if (numChars <= 0)
					return -1;
			}
			return buf[curChar++];
		}

		boolean isSpaceChar(int c) {
			return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
		}

		boolean isEndline(int c) {
			return c == '\n' || c == '\r' || c == -1;
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		int[] nextIntArray(int n) {
			int[] array = new int[n];
			for (int i = 0; i < n; i++)
				array[i] = nextInt();

			return array;
		}

		int[][] nextIntMap(int n, int m) {
			int[][] map = new int[n][m];
			for (int i = 0; i < n; i++) {
				map[i] = in.nextIntArray(m);
			}
			return map;
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		long[] nextLongArray(int n) {
			long[] array = new long[n];
			for (int i = 0; i < n; i++)
				array[i] = nextLong();

			return array;
		}

		long[][] nextLongMap(int n, int m) {
			long[][] map = new long[n][m];
			for (int i = 0; i < n; i++) {
				map[i] = in.nextLongArray(m);
			}
			return map;
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

		double[] nextDoubleArray(int n) {
			double[] array = new double[n];
			for (int i = 0; i < n; i++)
				array[i] = nextDouble();

			return array;
		}

		double[][] nextDoubleMap(int n, int m) {
			double[][] map = new double[n][m];
			for (int i = 0; i < n; i++) {
				map[i] = in.nextDoubleArray(m);
			}
			return map;
		}

		String next() {
			int c = read();
			while (isSpaceChar(c))
				c = read();
			StringBuilder res = new StringBuilder();
			do {
				res.appendCodePoint(c);
				c = read();
			} while (!isSpaceChar(c));
			return res.toString();
		}

		String[] nextStringArray(int n) {
			String[] array = new String[n];
			for (int i = 0; i < n; i++)
				array[i] = next();

			return array;
		}

		String nextLine() {
			int c = read();
			while (isEndline(c))
				c = read();
			StringBuilder res = new StringBuilder();
			do {
				res.appendCodePoint(c);
				c = read();
			} while (!isEndline(c));
			return res.toString();
		}
	}
}

import java.util.Scanner;

public class Main {

	static Scanner sc = new Scanner(System.in);

	public static void main(String[] arg) {
		while (true) {
			int K = sc.nextInt();
			if (K == 0) break;
			String s = sc.next();
			String t = sc.next();
			int[] d = new int[K];
			for (int i = 0; i < K; ++i) {
				d[i] = (10 + t.charAt(i) - s.charAt(i)) % 10;
			}
			System.out.println(solve(d, 0));
		}
	}

	static int solve(int[] d, int pos) {
		if (pos == d.length) return 0;
		if (d[pos] == 0) return solve(d, pos + 1);
		int prev = d[pos];
		int ret = 99;
		for (int i = pos; i < d.length; ++i) {
			d[i] = (d[i] + 10 - prev) % 10;
			ret = Math.min(ret, solve(d, pos + 1) + 1);
		}
		for (int i = pos; i < d.length; ++i) {
			d[i] = (d[i] + prev) % 10;
		}
		return ret;
	}
}

using static System.Math;
using System.Linq;
using System.Collections.Generic;
using System;

public class P
{
    public string s { get; set; }
    public int match { get; set; }
    public int rot { get; set; }
}
public class hello
{
    public static void Main()
    {
        while (true)
        {
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            string[] line = Console.ReadLine().Trim().Split(' ');
            var a = line[0];
            var b = line[1];
            getAns(a, b, n);
        }
    }
    static void getAns(string a, string b, int n)
    {
        var d = new Dictionary<string, int>();
        var ans = 10;
        var ch = "0123456789";
        var st = new Stack<P>();
        st.Push(new P { s = a, match = -1, rot = 0 });
        d[a] = 0;
        while (st.Count() > 0)
        {
            var w = st.Pop();
            if (w.s == b)
            {
                ans = Min(ans, w.rot);
                continue;
            }
            if (d.ContainsKey(w.s) && w.rot > d[w.s]) continue;
            var w2 = w.s.ToArray();
            var dif = b[w.match + 1] - w2[w.match + 1];
            if (dif == 0)
            {
                st.Push(new P { s = w.s, match = w.match + 1, rot = w.rot });
                continue;
            }
            for (int i = w.match + 1; i < n; i++)
            {
                var t = w2[i] - '0' + dif;
                if (t >= 10) t -= 10;
                else if (t < 0) t += 10;
                w2[i] = ch[t];
                var ns = new string(w2);
                if (!d.ContainsKey(ns) |
                   (d.ContainsKey(ns) && w.rot + 1 < d[ns]))
                {
                    st.Push(new P { s = ns, match = w.match + 1, rot = w.rot + 1 });
                    d[ns] = w.rot + 1;
                }
            }
        }
        Console.WriteLine(ans);
    }
}

