public class EqualDistancePoint {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        List<Line> lines = new ArrayList<>();

        while (true) {
            System.out.println("Enter line (x1 y1 x2 y2) or 'done' to finish:");
            String input = scanner.nextLine();
            if (input.equals("done")) break;

            String[] coords = input.split(" ");
            if (coords.length != 4) {
                System.out.println("Invalid input. Try again.");
                continue;
            }

            double x1 = Double.parseDouble(coords[0]);
            double y1 = Double.parseDouble(coords[1]);
            double x2 = Double.parseDouble(coords[2]);
            double y2 = Double.parseDouble(coords[3]);
            lines.add(new Line(x1, y1, x2, y2));
        }

        Point2D result = findEqualDistancePoint(lines);
        if (result == null) {
            System.out.println("No solution exists.");
        } else {
            System.out.printf("Point: (%.2f, %.2f)%n", result.getX(), result.getY());
        }
    }

    static Point2D findEqualDistancePoint(List<Line> lines) {
        if (lines.size() < 2) return null;

        for (int i = 0; i < lines.size(); i++) {
            for (int j = i + 1; j < lines.size(); j++) {
                Point2D candidate = findIntersection(lines.get(i), lines.get(j));
                if (candidate != null && isEqualDistance(candidate, lines)) {
                    return candidate;
                }
            }
        }

        return null;
    }

    static Point2D findIntersection(Line l1, Line l2) {
        double x1 = l1.x1, y1 = l1.y1, x2 = l1.x2, y2 = l1.y2;
        double x3 = l2.x1, y3 = l2.y1, x4 = l2.x2, y4 = l2.y2;

        double denominator = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
        if (Math.abs(denominator) < 1e-9) return null;

        double px = ((x1 * y2 - y1 * x2) * (x3 - x4) - (x1 - x2) * (x3 * y4 - y3 * x4)) / denominator;
        double py = ((x1 * y2 - y1 * x2) * (y3 - y4) - (y1 - y2) * (x3 * y4 - y3 * x4)) / denominator;

        return new Point2D.Double(px, py);
    }

    static boolean isEqualDistance(Point2D point, List<Line> lines) {
        double firstDistance = distanceToLine(point, lines.get(0));
        for (Line line : lines) {
            if (Math.abs(distanceToLine(point, line) - firstDistance) > 1e-9) {
                return false;
            }
        }
        return true;
    }

    static double distanceToLine(Point2D point, Line line) {
        double x0 = point.getX(), y0 = point.getY();
        double x1 = line.x1, y1 = line.y1, x2 = line.x2, y2 = line.y2;

        return Math.abs((y2 - y1) * x0 - (x2 - x1) * y0 + x2 * y1 - y2 * x1) / 
               Math.sqrt(Math.pow(y2 - y1, 2) + Math.pow(x2 - x1, 2));
    }

    static class Line {
        double x1, y1, x2, y2;
        Line(double x1, double y1, double x2, double y2) {
            this.x1 = x1;
            this.y1 = y1;
            this.x2 = x2;
            this.y2 = y2;
        }
    }
}

