#define MAX_LINES 100
#define EPSILON 1e-9

typedef struct {
    double a, b, c;  // Line in form ax + by + c = 0
} Line;

double distance_point_to_line(double x, double y, Line* line) {
    return fabs(line->a * x + line->b * y + line->c) / 
           sqrt(line->a * line->a + line->b * line->b);
}

int main() {
    int n;
    Line lines[MAX_LINES];

    printf("Enter number of lines: ");
    scanf("%d", &n);

    for (int i = 0; i < n; i++) {
        printf("Enter line %d (a b c for ax + by + c = 0): ", i+1);
        scanf("%lf %lf %lf", &lines[i].a, &lines[i].b, &lines[i].c);
    }

    int solutions = 0;
    double solution_x = 0, solution_y = 0;

    for (double x = -1000; x <= 1000; x += 0.1) {
        for (double y = -1000; y <= 1000; y += 0.1) {
            int is_solution = 1;
            double first_distance = distance_point_to_line(x, y, &lines[0]);

            for (int i = 1; i < n; i++) {
                double current_distance = distance_point_to_line(x, y, &lines[i]);
                if (fabs(current_distance - first_distance) > EPSILON) {
                    is_solution = 0;
                    break;
                }
            }

            if (is_solution) {
                solutions++;
                solution_x = x;
                solution_y = y;

                if (solutions > 1) break;
            }
        }
        if (solutions > 1) break;
    }

    if (solutions == 0) {
        printf("No solution exists.\n");
    } else if (solutions > 1) {
        printf("Multiple solutions exist.\n");
    } else {
        printf("Solution point: (%.2f, %.2f)\n", solution_x, solution_y);
    }

    return 0;
}

