};

double distance(const Line& line, const Point& point) {
    return std::abs(line.a * point.x + line.b * point.y + line.c) / 
           std::sqrt(line.a * line.a + line.b * line.b);
}

Point intersect(const Line& l1, const Line& l2) {
    double det = l1.a * l2.b - l2.a * l1.b;
    if (std::abs(det) < EPS) {
        return {std::numeric_limits<double>::quiet_NaN(), 
                std::numeric_limits<double>::quiet_NaN()};
    }
    return {
        (l1.b * l2.c - l2.b * l1.c) / det,
        (l2.a * l1.c - l1.a * l2.c) / det
    };
}

