        for (int j = i + 1; j < n; ++j) {
            Point p = intersect(lines[i], lines[j]);
            if (std::isnan(p.x)) continue;

            bool valid = true;
            double dist = distance(lines[0], p);

            for (const auto& line : lines) {
                if (std::abs(distance(line, p) - dist) > EPS) {
                    valid = false;
                    break;
                }
            }

            if (valid) candidates.push_back(p);
        }
