from sympy.geometry import Line, Point

def find_equidistant_point(lines):
    if not lines or len(lines) < 2:
        return "No solutions"

    # To handle only 2D case
    x, y = symbols('x y')
    points = []

    for line in lines:
        if ' ' in line:
            line = line.split()
        else:
            return "Invalid line format"

        try:
            a, b, c = map(float, line)
            if a == 0 and b == 0:
                return "Invalid line parameters"
        except:
            return "Invalid line parameters"

        # Get a point on the line
        if b != 0:
            pt = Point(0, -c / b)
        elif a != 0:
            pt = Point(-c / a, 0)
        else:
            return "Invalid line parameters"

        # Normal vector to line
        normal = (a, b)
        points.append((pt, normal))

    n = len(points)
    sym_points = [symbols(f'x{i}, y{i}') for i in range(n)]
    eqs = []

    for i, (spi, ni) in enumerate(zip(sym_points, points)):
        pi, ni = points[i]
        xi, yi = spi

        for j in range(i + 1, n):
            pj, nj = points[j]
            xj, yj = sym_points[j]

            dist_i = abs(a * xi + b * yi + c) / sqrt(a * a + b * b)
            dist_j = abs(a * xj + b * yj + c) / sqrt(a * a + b * b)

            eq = Eq(dist_i, dist_j)
            eqs.append(eq)

    solution = solve(eqs, [x, y])

    if not solution:
        return "No solutions"
    elif isinstance(solution, dict):
        return f"Equidistant Point: ({solution[x]}, {solution[y]})"
    else:
        return "Multiple solutions"

