#define EPSILON 1e-9

typedef struct {
    double a, b, c;
} Line;

// Function to check if two doubles are approximately equal
int approximatelyEqual(double d1, double d2) {
    return fabs(d1 - d2) < EPSILON;
}

// Function to find the intersection point of two lines
int findIntersection(Line l1, Line l2, double* x, double* y) {
    double det = l1.a * l2.b - l2.a * l1.b;
    if (approximatelyEqual(det, 0)) {
        return 0; // Lines are parallel
    }
    *x = (l2.c * l1.b - l1.c * l2.b) / det;
    *y = (l1.c * l2.a - l2.c * l1.a) / det;
    return 1;
}

// Function to check if a point is equidistant from a set of lines
int isEquidistant(Line lines[], int n, double x, double y) {
    double distance = fabs(lines[0].a * x + lines[0].b * y + lines[0].c) /
                      sqrt(lines[0].a * lines[0].a + lines[0].b * lines[0].b);
    for (int i = 1; i < n; i++) {
        double d = fabs(lines[i].a * x + lines[i].b * y + lines[i].c) /
                   sqrt(lines[i].a * lines[i].a + lines[i].b * lines[i].b);
        if (!approximatelyEqual(d, distance)) {
            return 0;
        }
    }
    return 1;
}

int main() {
    int n;
    printf("Enter the number of lines: ");
    scanf("%d", &n);

    if (n < 2) {
        printf("No solutions or multiple solutions.\n");
        return 0;
    }

    Line lines[n];
    printf("Enter the lines in the format a, b, c (ax + by + c = 0):\n");
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf %lf", &lines[i].a, &lines[i].b, &lines[i].c);
    }

    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            double x, y;
            if (findIntersection(lines[i], lines[j], &x, &y)) {
                if (isEquidistant(lines, n, x, y)) {
                    printf("Equidistant point: (%.6f, %.6f)\n", x, y);
                    return 0;
                }
            }
        }
    }

    printf("No solutions or multiple solutions.\n");
    return 0;
}

