using namespace std;

struct Line {
    double a, b, c;
};

bool isParallel(const Line &l1, const Line &l2) {
    return fabs(l1.a * l2.b - l2.a * l1.b) < 1e-9;
}

pair<double, double> findIntersection(const Line &l1, const Line &l2) {
    double det = l1.a * l2.b - l2.a * l1.b;
    return {
        (l2.b * l1.c - l1.b * l2.c) / det,
        (l1.a * l2.c - l2.a * l1.c) / det
    };
}

int main() {
    int n;
    cout << "Enter the number of lines: ";
    cin >> n;

    vector<Line> lines(n);
    cout << "Enter the coefficients (a, b, c) for each line:" << endl;
    for (int i = 0; i < n; ++i) {
        cin >> lines[i].a >> lines[i].b >> lines[i].c;
    }

    // Check for parallel lines
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            if (isParallel(lines[i], lines[j])) {
                cout << "No solution: lines are parallel or coincident." << endl;
                return 0;
            }
        }
    }

    // Assuming n >= 2, since a single line does not define a unique point
    pair<double, double> intersection = findIntersection(lines[0], lines[1]);

    // Check if this intersection point satisfies being equidistant
    double distance = abs(lines[0].a * intersection.first + lines[0].b * intersection.second + lines[0].c) / 
                      sqrt(lines[0].a * lines[0].a + lines[0].b * lines[0].b);

    for (int i = 1; i < n; ++i) {
        double currentDistance = abs(lines[i].a * intersection.first + lines[i].b * intersection.second + lines[i].c) / 
                                 sqrt(lines[i].a * lines[i].a + lines[i].b * lines[i].b);
        if (fabs(currentDistance - distance) > 1e-9) {
            cout << "No solution: no common equidistant point." << endl;
            return 0;
        }
    }

    cout << "Equidistant point: (" << intersection.first << ", " << intersection.second << ")" << endl;
    return 0;
}
