#include <iostream>
#include <queue>
#include <cstring>
#include <algorithm>
using namespace std;

int dx[]={-1,0,1,0};
int dy[]={0,-1,0,1};

bool lp[30][30];
bool ck[30][30][30][30];
int dist[30][30][30][30];
int relt[30][30][30][30];
int memo[30][30][30][30];
int W,H,gx[900],gy[900],gN;
char f[31][31];

class State
{
public:
	int x,y,c;
	State(int x, int y, int c)
	:x(x),y(y),c(c)
	{}
};

void bfs(int x, int y)
{
	bool hs[30][30]={0};

	queue<State> q;
	q.push(State(x,y,0));
	hs[x][y]=1;

	while(!q.empty())
	{
		State s=q.front(); q.pop();
		dist[x][y][s.x][s.y]=s.c;

		for(int i=0; i<4; i++)
		{
			int tx=s.x+dx[i], ty=s.y+dy[i];
			if(tx<0||ty<0||tx>=W||ty>=H) continue;
			if(f[tx][ty]=='#') continue;
			if(hs[tx][ty]) continue;

			hs[tx][ty]=1;

			q.push(State(tx,ty,s.c+1));
		}
	}
}

void calcD()
{
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		bfs(j,i);
	}
}

bool loop(int qx, int qy, int d)
{
	int sx=qx+dx[d], sy=qy+dy[d];
	if(sx<0||sy<0||sx>=W||sy>=H) return false;
	if(f[sx][sy]=='#') return false;

	bool hs[30][30]={0};

	queue<State> q;
	q.push(State(sx,sy,0));
	hs[qx][qy]=1;
	hs[sx][sy]=1;

	while(!q.empty())
	{
		State s=q.front(); q.pop();

		for(int i=0; i<4; i++)
		{
			int tx=s.x+dx[i], ty=s.y+dy[i];
			if(tx<0||ty<0||tx>=W||ty>=H) continue;
			if(f[tx][ty]=='#') continue;
			if(tx==qx&&ty==qy)
			{
				if(s.c==0) continue;
				else return true;
			}
			if(hs[tx][ty]) continue;

			hs[tx][ty]=1;

			q.push(State(tx,ty,s.c+1));
		}
	}

	return false;
}

int judge(int qx, int qy, int ax, int ay)
{	
	for(int k=0; k<gN; k++)
	{
		if(dist[qx][qy][gx[k]][gy[k]] < dist[ax][ay][gx[k]][gy[k]]) return 1;
	}

	if(lp[qx][qy]) return 0;
	return -1;
}

void Search()
{
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		for(int k=0; k<4; k++)
		{
			if(loop(j,i,k))
			{
				 lp[j][i]=true;
				 break;
			}
		}

		for(int k=0; k<H; k++)
		for(int l=0; l<W; l++)
		{
			if(f[l][k]=='#') continue;
			relt[j][i][l][k]=judge(j,i,l,k);
		}
	}
}

int Aphase(int a, int b, int x, int y);
int Qphase(int a, int b, int x, int y);

int Aphase(int a, int b, int x, int y, int d)
{
	if(ck[a][b][x][y]) return memo[a][b][x][y];
	int ret=relt[a][b][x][y];
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(dist[x][y][j][i]==-1) continue;

		if(relt[a][b][j][i]>relt[a][b][x][y]) continue;
		if(dist[x][y][j][i]<=d)
		{
			ret=min(ret, relt[a][b][j][i]);
		}
	}

	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(dist[x][y][j][i]==-1) continue;
		if(ck[a][b][j][i]) 
		{
			ret=min(ret, memo[a][b][j][i]);
			continue;
		}

		if(relt[a][b][j][i]>ret) continue;
		if(dist[x][y][j][i]<=d)
		{
			ret=min(ret, Qphase(a,b,j,i));
		}

	}
	return ret;
}

int Qphase(int a, int b, int x, int y)
{
	if(ck[a][b][x][y]) return memo[a][b][x][y];
	ck[a][b][x][y]=1;

	memo[a][b][x][y]=relt[a][b][x][y];
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		if(dist[a][b][j][i]>=dist[x][y][j][i]) continue;
		if(dist[a][b][j][i]==-1) continue;

		memo[a][b][x][y]=max(memo[a][b][x][y], Aphase(j,i,x,y,dist[a][b][j][i]));
	}

	return memo[a][b][x][y];
}

int main()
{
	while(cin >> W >> H, (W||H))
	{
		int ax,ay,qx,qy;
		gN=0;
		memset(lp,0,sizeof(lp));
		memset(dist, -1, sizeof(dist));
		memset(ck,0,sizeof(ck));
		for(int i=0; i<H; i++)
		for(int j=0; j<W; j++)
		{
			cin >> f[j][i];
			if(f[j][i]=='A')
			{
				ax=j; ay=i;
			}
			if(f[j][i]=='Q')
			{
				qx=j; qy=i;
			}
			if(f[j][i]=='E')
			{
				gx[gN]=j;
				gy[gN]=i;
				gN++;
			}
		}

		calcD();
		Search();

		int ret=Qphase(qx,qy,ax,ay);
		if(ret==-1) 	cout << "Army can catch Queen." << endl;
		else if(ret==0)	cout << "Queen can not escape and Army can not catch Queen." << endl;
		else			cout << "Queen can escape." << endl;
	}
}

#include <iostream>
#include <queue>
#include <cstring>
#include <algorithm>
using namespace std;

int dx[]={-1,0,1,0};
int dy[]={0,-1,0,1};

bool ck[30][30][30][30]; // ax,ay qx,qy
int dist[30][30][30][30]; // xy to x'y'
int relt[30][30][30][30];
int memo[30][30][30][30];
int W,H,gx[900],gy[900],gN;
char f[31][31];

class State
{
public:
	int x,y,c;
	State(int x, int y, int c)
	:x(x),y(y),c(c)
	{}
};

void bfs(int x, int y)
{
	bool hs[30][30]={0};

	queue<State> q;
	q.push(State(x,y,0));
	hs[x][y]=1;

	while(!q.empty())
	{
		State s=q.front(); q.pop();
		dist[x][y][s.x][s.y]=s.c;

		for(int i=0; i<4; i++)
		{
			int tx=s.x+dx[i], ty=s.y+dy[i];
			if(tx<0||ty<0||tx>=W||ty>=H) continue;
			if(f[tx][ty]=='#') continue;
			if(hs[tx][ty]) continue;

			hs[tx][ty]=1;

			q.push(State(tx,ty,s.c+1));
		}
	}
}

void calcD()
{
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		bfs(j,i);
	}
}

int judge(int qx, int qy, int ax, int ay)
{
	int cnt=0;
	for(int k=0; k<gN; k++)
	{
		if(dist[qx][qy][gx[k]][gy[k]] < dist[ax][ay][gx[k]][gy[k]]) return 1;

		if(dist[qx][qy][gx[k]][gy[k]] == dist[ax][ay][gx[k]][gy[k]]) cnt++;
	}
	if(cnt>=2) return 0;
	return -1;
}

void Search()
{
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		for(int k=0; k<H; k++)
		for(int l=0; l<W; l++)
		{

			if(f[l][k]=='#') continue;
			relt[j][i][l][k]=judge(j,i,l,k);
		}
	}
}

int Aphase(int a, int b, int x, int y);
int Qphase(int a, int b, int x, int y);

int Aphase(int a, int b, int x, int y, int d)
{
	if(ck[a][b][x][y]) return memo[a][b][x][y];
	memo[a][b][x][y]=relt[a][b][x][y];
	ck[a][b][x][y]=1;

	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		if(dist[x][y][j][i]<=d)
		{
			bool g=true;
			for(int k=0; k<gN; k++)
			{
				if(dist[a][b][gx[k]][gy[k]] < dist[j][i][gx[k]][gy[k]])
				{
					g=false;
					break;
				}
			}
			if(!g) continue;

			memo[a][b][x][y]=min(memo[a][b][x][y], Qphase(a,b,j,i));

		}
	}
	return memo[a][b][x][y];
}

int Qphase(int a, int b, int x, int y)
{
	int ret=relt[a][b][x][y];
	//if(ck[a][b][x][y]) return ck[a][b][x][y];
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		if(dist[a][b][j][i]>=dist[x][y][j][i]) continue;
		if(ck[j][i][x][y]) return memo[j][i][x][y];

		ret=max(ret, Aphase(j,i,x,y,dist[a][b][j][i]));
	}

	return ret;
}

int main()
{
	while(cin >> W >> H, (W||H))
	{
		int ax,ay,qx,qy;
		gN=0;
		memset(ck,0,sizeof(ck));
		for(int i=0; i<H; i++)
		for(int j=0; j<W; j++)
		{
			cin >> f[j][i];
			if(f[j][i]=='A')
			{
				ax=j; ay=i;
			}
			if(f[j][i]=='Q')
			{
				qx=j; qy=i;
			}
			if(f[j][i]=='E')
			{
				gx[gN]=j;
				gy[gN]=i;
				gN++;
			}
		}

		calcD();
		Search();

		int ret=Qphase(qx,qy,ax,ay);
		if(ret==-1) 	cout << "Army can catch Queen." << endl;
		else if(ret==0)	cout << "Queen can not escape and Army can not catch Queen." << endl;
		else			cout << "Queen can escape." << endl;
	}
}

#include <iostream>
#include <cstdio>
#include <vector>
#include <queue>
#include <cmath>
#include <algorithm>
#include <functional>
using namespace std;
#define rep(i,n) for(int i=0;i<(n);++i)
#define rep1(i,n) for(int i=1;i<=(n);++i)
#define all(c) (c).begin(),(c).end()
#define pb push_back
#define fs first
#define sc second
#define show(x) cout << #x << " = " << x << endl;
typedef pair<int,int> P;
int h,w,qx,qy,ax,ay;
string s[30];
short int dp[30][30][30][30][2];	//1->Qwin,-1->Awin.0->?
//qx,qy,ax,ay,turn=0->queen
int dx[5]={0,1,0,-1,0},dy[5]={0,0,1,0,-1};
vector<P> exits;
bool is(int x,int y){
	if(x<0||h<=x||y<0||w<=y||s[x][y]=='#') return false;
	return true;
}
void init(){
	exits.clear();
	rep(i,h) rep(j,w) rep(k,h) rep(l,w) rep(turn,2) dp[i][j][k][l][turn]=0;
}
void showdp(int i,int j,int k,int l,int turn,int value){
	printf("dp[%d][%d][%d][%d][%d]=%d\n",i,j,k,l,turn,value);
}
int main(){
	while(true){
		cin>>w>>h;
		if(w==0) break;
		init();
		rep(i,h) cin>>s[i];
		rep(i,h) rep(j,w){
			if(s[i][j]=='Q') qx=i,qy=j;
			if(s[i][j]=='A') ax=i,ay=j;
			if(s[i][j]=='E') exits.pb(P(i,j));
		}
		bool update=true;
		for(P ex : exits){
			int x=ex.fs,y=ex.sc;
			rep(i,h) rep(j,w) dp[x][y][i][j][0]=1;
		}
		rep(i,h) rep(j,w) rep(turn,2) dp[i][j][i][j][turn]=-1;
		while(update){
			update=false;
			//turn=0
			rep(i,h) rep(j,w) rep(k,h) rep(l,w){
				if(dp[i][j][k][l][0]!=0) continue;
				bool allcant=true,canwin=false;
				rep(di,5){
					int nx=i+dx[di],ny=j+dy[di];
					if(!is(nx,ny)) continue;
					if(dp[nx][ny][k][l][1]==1) canwin=true;
					if(dp[nx][ny][k][l][1]!=-1) allcant=false;
				}
				if(canwin){
					dp[i][j][k][l][0]=1;
//					showdp(i,j,k,l,0,1);
					update=true;
					continue;
				}
				if(allcant){
					dp[i][j][k][l][0]=-1;
//					showdp(i,j,k,l,0,-1);
					update=true;
					continue;
				}
			}
			//turn=1
			rep(i,h) rep(j,w) rep(k,h) rep(l,w){
				if(dp[i][j][k][l][1]!=0) continue;
				bool allcant=true,canwin=false;
				rep(di,5){
					int nx=k+dx[di],ny=l+dy[di];
					if(!is(nx,ny)) continue;
					if(dp[i][j][nx][ny][0]==-1) canwin=true;
					if(dp[i][j][nx][ny][0]!=1) allcant=false;
				}
				if(canwin){
					dp[i][j][k][l][1]=-1;
//					showdp(i,j,k,l,1,-1);
					update=true;
					continue;
				}
				if(allcant){
					dp[i][j][k][l][1]=1;
//					showdp(i,j,k,l,1,1);
					update=true;
					continue;
				}
			}
		}
//		rep(i,h) rep(j,w) rep(k,h) rep(l,w) rep(turn,2) showdp(i,j,k,l,turn,dp[i][j][k][l][turn]);
		switch(dp[qx][qy][ax][ay][0]){
			case -1:
				puts("Army can catch Queen.");
				break;
			case 0:
				puts("Queen can not escape and Army can not catch Queen.");
				break;
			case 1:
				puts("Queen can escape.");
				break;
		}
	}
}

#include "bits/stdc++.h"
using namespace std;

typedef vector<vector<int>> Graph;

vector<int> solve(const Graph&g) {
	vector<int>nums(g.size());
	map<int, vector<int>> revg;
	for (int i = 0; i < g.size();++i){
		auto es(g[i]);
		nums[i] = es.size();
		for (auto e : es) {
			revg[e].push_back(i);
		}

	}

	//1 : win   0 : draw   -1 :loss
	vector<int>anss(g.size());
	queue<int>que;
	for (int i = 0; i < g.size(); ++i) {
		if (!nums[i]) {
			que.push(i);
			anss[i] = -1;
		}
	}
	while (!que.empty()) {
		int now(que.front());
		que.pop();
		if (anss[now] == 1) {
			for (auto reve : revg[now]) {
				nums[reve]--;
				if (!nums[reve]&&anss[reve]==0) {
					anss[reve] = -1;
					que.push(reve);
				}
			}
		}
		else if (anss[now] == -1) {
			for (auto reve : revg[now]) {
				if (anss[reve] == 0) {
					anss[reve] = 1;
					que.push(reve);
				}
			}
		}
		else {
			assert(false);
		}
	}
	return anss;
}
int SIZE = 30;
int gethash(int ax, int ay, int bx, int by, int c) {
	return (ax-1)* SIZE * SIZE * SIZE * 2 + (ay-1)* SIZE * SIZE * 2 + (bx-1) * SIZE * 2 + (by-1) * 2 + c+1;
}

int main() {
	while (true) {
		int W, H; cin >> W >> H;
		if (!W)break;
		vector<vector<int>>field(H + 2, vector<int>(W + 2,-1));
		int sqx, sqy, sax, say;
		for (int i = 0; i < H; ++i) {
			string st; cin >> st;
			for (int j = 0; j < W; ++j) {
				switch (st[j]) {
				case 'Q':
					field[i + 1][j + 1] = 0;
					sqx = j + 1; sqy = i + 1;
					break;
				case 'A':
					field[i + 1][j + 1] = 0;
					sax = j + 1; say = i + 1;
					break;
				case '#':
					break;
				case '.':
					field[i + 1][j + 1] = 0;
					break;
				case 'E':
					field[i + 1][j + 1] = 2;
					break;
				default:
					assert(false);
				}
			}
		}
		SIZE = max(H, W);
		Graph g(SIZE * SIZE * SIZE*SIZE*2+1);
		int dx[] = { -1,0,1,0,0 };
		int dy[] = { 0,1,0,-1,0 };
		for (int qx = 1; qx < W + 1; ++qx) {
			for (int qy = 1; qy < H + 1; ++qy) {
				if (field[qy][qx] == -1)continue;
				for (int ax = 1; ax < W + 1; ++ax) {
					for (int ay = 1; ay < H + 1; ++ay) {
						if (field[ay][ax] == -1)continue;
						for (int nt = 0; nt < 2; ++nt) {
							int nowhash = gethash(qx, qy, ax, ay, nt);
							if (qx == ax&&qy == ay) {
								if (nt) {
									continue;
								}
								else {
									g[nowhash].push_back(0);
								}
							}
							if (field[qy][qx] == 2&&nt) {
								g[nowhash].push_back(0);
							}
							for (int way = 0; way < 5; ++way) {
								int nqx(qx), nqy(qy), nax(ax), nay(ay);
								if (nt) {
									nqx += dx[way]; nqy += dy[way];
									if (field[nqy][nqx]==-1) continue;
								}
								else {
									nax += dx[way]; nay += dy[way];
									if ((field[nay][nax]==-1)) continue;
								}
								if (nax == nqx&&nay == nqy) {
									if (nt) {
										continue;
									}
									else {
										g[nowhash].push_back(0);
									}
								}
								int nexthash = gethash(nqx, nqy, nax, nay, !nt);
								g[nowhash].push_back(nexthash);

							}
						}
					}
				}
			}
		}
		auto anss=solve(g);
		int ans = anss[gethash(sqx, sqy, sax, say, 1)];
		if (ans == 0)cout << "Queen can not escape and Army can not catch Queen." << endl;
		else if (ans == 1)cout << "Queen can escape." << endl;
		else cout << "Army can catch Queen." << endl;
	}


	return 0;
}

