			rep(i,h) rep(j,w) dp[x][y][i][j][0]=1;
		}
		rep(i,h) rep(j,w) rep(turn,2) dp[i][j][i][j][turn]=-1;
		while(update){
			update=false;
			//turn=0
			rep(i,h) rep(j,w) rep(k,h) rep(l,w){
				if(dp[i][j][k][l][0]!=0) continue;
				bool allcant=true,canwin=false;
				rep(di,5){
					int nx=i+dx[di],ny=j+dy[di];
					if(!is(nx,ny)) continue;
					if(dp[nx][ny][k][l][1]==1) canwin=true;
					if(dp[nx][ny][k][l][1]!=-1) allcant=false;
				}
				if(canwin){
					dp[i][j][k][l][0]=1;
//					showdp(i,j,k,l,0,1);
					update=true;
					continue;
				}
				if(allcant){
					dp[i][j][k][l][0]=-1;
//					showdp(i,j,k,l,0,-1);
					update=true;
					continue;
				}
			}
			//turn=1
			rep(i,h) rep(j,w) rep(k,h) rep(l,w){
				if(dp[i][j][k][l][1]!=0) continue;
				bool allcant=true,canwin=false;
				rep(di,5){
					int nx=k+dx[di],ny=l+dy[di];
					if(!is(nx,ny)) continue;
					if(dp[i][j][nx][ny][0]==-1) canwin=true;
					if(dp[i][j][nx][ny][0]!=1) allcant=false;
				}
				if(canwin){
					dp[i][j][k][l][1]=-1;
//					showdp(i,j,k,l,1,-1);
					update=true;
					continue;
				}
				if(allcant){
					dp[i][j][k][l][1]=1;
//					showdp(i,j,k,l,1,1);
					update=true;
					continue;
				}
			}
		}
//		rep(i,h) rep(j,w) rep(k,h) rep(l,w) rep(turn,2) showdp(i,j,k,l,turn,dp[i][j][k][l][turn]);
		switch(dp[qx][qy][ax][ay][0]){
			case -1:
				puts("Army can catch Queen.");
				break;
			case 0:
				puts("Queen can not escape and Army can not catch Queen.");
				break;
			case 1:
				puts("Queen can escape.");
				break;
		}
	}
}

