using namespace std;
const int nmax = 30 + 1;
const int dx[5] = {0, 0, 1, -1, 0};
const int dy[5] = {1, -1, 0, 0, 0};

struct state {
	int x1, y1, x2, y2;
	bool turn; // 0 for Q ed
	state() {};
	state(int _x1, int _y1, int _x2, int _y2, bool _turn) {
		x1 = _x1, x2 = _x2, y1 = _y1, y2 = _y2, turn = _turn;
	}
	void init(int _x1, int _y1, int _x2, int _y2, bool _turn) {
		x1 = _x1, x2 = _x2, y1 = _y1, y2 = _y2, turn = _turn;
	}
};

bool f[nmax][nmax][nmax][nmax][2]; // 0 for lose
vector<state> Q;
bool ed[nmax][nmax][nmax][nmax][2];
char a[nmax][nmax];
int n, m;

int check(state &s)
{
	int x1, x2, y1, y2;
	int nxtt = s.turn ^ 1;
	bool all = 1, exist = 0;
	if (s.turn == 0) {
		x1 = s.x1, y1 = s.y1;
		for (int j = 0; j < 5; ++j) {
			x2 = s.x2 + dx[j], y2 = s.y2 + dy[j];
			if (x2 <= 0 || x2 > n || y2 <= 0 || y2 > m || a[x2][y2] == '#') continue;
			if (!ed[x1][y1][x2][y2][nxtt]) {
				all = 0;
				continue;
			}
			if (f[x1][y1][x2][y2][nxtt] == 0)
				exist = 1, all = 0;
		}
	}
	else {
		for (int i = 0; i < 5; ++i) {
			x1 = s.x1 + dx[i], y1 = s.y1 + dy[i];
			if (x1 <= 0 || x1 > n || y1 <= 0 || y1 > m || a[x1][y1] == '#') continue;
			x2 = s.x2, y2 = s.y2;
			if (!ed[x1][y1][x2][y2][nxtt]) {
				all = 0;
				continue;
			}
			if (f[x1][y1][x2][y2][nxtt] == 1)
				exist = 1, all = 0;
		}
	}
	if (all)
		return nxtt;
	else if (exist)
		return s.turn;
	else return 2;
}

