				for (int i = 0; i < 5; ++i) {
					x1 = now.x1 + dx[i], y1 = now.y1 + dy[i];
					if (x1 <= 0 || x1 > n || y1 <= 0 || y1 > m || a[x1][y1] == '#') continue;
					x2 = now.x2, y2 = now.y2;
					if (ed[x1][y1][x2][y2][nxtt]) continue;
					tmp.init(x1, y1, x2, y2, nxtt);
					if ((tmpf = check(tmp)) != 2) {
						ed[x1][y1][x2][y2][nxtt] = 1;
						f[x1][y1][x2][y2][nxtt] = tmpf;
						Q.push_back(tmp);
					}
				}
			}
		}
		int lx, ly, rx, ry;
		for (int i = 1; i <= n; ++i)
			for (int j = 1; j <= m; ++j) {
				if (a[i][j] == 'Q')
					lx = i, ly = j;
				if (a[i][j] == 'A')
					rx = i, ry = j;
			}
		if (!ed[lx][ly][rx][ry][1])
			printf("Queen can not escape and Army can not catch Queen.\n"); // tie
		else
			if (f[lx][ly][rx][ry][1])
				printf("Queen can escape.\n"); //win
			else
				printf("Army can catch Queen.\n"); //lose
	}
	return 0;
}

#include <algorithm>
#include <cstdlib>
#include <iostream>
#include <vector>
using namespace std;

template<class T> inline void chmin(T &a, const T &b) { if(a > b) a = b; }
template<class T> inline void chmax(T &a, const T &b) { if(a < b) a = b; }

typedef vector<vector<int>> graph;

constexpr int INF = (1 << 29);
constexpr int MAX_N = 900;

enum {Q, A, NUM};

int dp[MAX_N][MAX_N][NUM];

inline int get_index(int x, int y, int w) {
	return x + y * w;
}

inline void add_edge(int a, int b, graph &G) {
	G[a].emplace_back(b);
	G[b].emplace_back(a);
}

inline string solve(int q, int a, const vector<int> &g, const graph &G) {
	fill_n((int *)dp, sizeof(dp) / sizeof(int), INF / 2);

	const int n = G.size();
	for(int v = 0; v < n; ++v) {
		dp[v][v][Q] = dp[v][v][A] = INF;
	}

	for(const auto &e : g) {
		for(int v = 0; v < n; ++v) {
			if(e != v) dp[e][v][Q] = 0;
		}
	}

	for(int iter = 0; iter < 2 * n; ++iter) {
		for(int v = 0; v < n; ++v) {
			for(int u = 0; u < n; ++u) {
				if(v == u) continue;

				// queen turn
				if(dp[v][u][Q] != 0) {
					dp[v][u][Q] = INF;
					for(const auto &to : G[v]) {
						chmin(dp[v][u][Q], dp[to][u][A]);
					}
				}

				// army turn
				dp[v][u][A] = 0;
				for(const auto &to : G[u]) {
					chmax(dp[v][u][A], dp[v][to][Q] + 1);
				}
			}
		}
	}

	if(dp[q][a][Q] >= INF) return "Army can catch Queen.";
	if(dp[q][a][Q] >= INF / 2) return "Queen can not escape and Army can not catch Queen.";
	return "Queen can escape.";

}

int main() {
	cin.tie(nullptr);
	ios::sync_with_stdio(false);

	for(int w, h; cin >> w >> h && w;) {
		vector<string> field(h);
		for(auto &e : field) cin >> e;

		const int n = w * h;
		int q, a;
		vector<int> g;

		graph G(n);

		for(int i = 0; i < h; ++i) {
			for(int j = 0; j < w; ++j) {
				if(field[i][j] == '#') continue;

				const int v = get_index(j, i, w);

				switch(field[i][j]) {
				case 'Q': q = v; break;
				case 'A': a = v; break;
				case 'E': g.emplace_back(v); break;
				}

				add_edge(v, v, G);

				if(i < h - 1) {
					if(field[i + 1][j] != '#') {
						add_edge(v, get_index(j, i + 1, w), G);
					}
				}

				if(j < w - 1) {
					if(field[i][j + 1] != '#') {
						add_edge(v, get_index(j + 1, i, w), G);
					}
				}
			}
		}

		cout << solve(q, a, g, G) << endl;
	}

	return EXIT_SUCCESS;
}

#include "bits/stdc++.h"
using namespace std;

typedef vector<vector<int>> Graph;

vector<int> solve(const Graph&g) {
	vector<int>nums(g.size());
	Graph revg(g.size());
	for (int i = 0; i < g.size();++i){
		auto es(g[i]);
		nums[i] = es.size();
		for (auto e : es) {
			revg[e].push_back(i);
		}

	}

	//1 : win   0 : draw   -1 :loss
	vector<int>anss(g.size());
	queue<int>que;
	for (int i = 0; i < g.size(); ++i) {
		if (!nums[i]) {
			que.push(i);
			anss[i] = -1;
		}
	}
	while (!que.empty()) {
		int now(que.front());
		que.pop();
		if (anss[now] == 1) {
			for (auto reve : revg[now]) {
				nums[reve]--;
				if (!nums[reve]&&anss[reve]==0) {
					anss[reve] = -1;
					que.push(reve);
				}
			}
		}
		else if (anss[now] == -1) {
			for (auto reve : revg[now]) {
				if (anss[reve] == 0) {
					anss[reve] = 1;
					que.push(reve);
				}
			}
		}
		else {
			assert(false);
		}
	}
	return anss;
}
const int SIZE = 30;
int gethash(int ax, int ay, int bx, int by, int c) {
	return (ax-1)* SIZE * SIZE * SIZE * 2 + (ay-1)* SIZE * SIZE * 2 + (bx-1) * SIZE * 2 + (by-1) * 2 + c+1;
}

int main() {
	while (true) {
		int W, H; cin >> W >> H;
		if (!W)break;
		vector<vector<int>>field(H + 2, vector<int>(W + 2,-1));
		int sqx, sqy, sax, say;
		for (int i = 0; i < H; ++i) {
			string st; cin >> st;
			for (int j = 0; j < W; ++j) {
				switch (st[j]) {
				case 'Q':
					field[i + 1][j + 1] = 0;
					sqx = j + 1; sqy = i + 1;
					break;
				case 'A':
					field[i + 1][j + 1] = 0;
					sax = j + 1; say = i + 1;
					break;
				case '#':
					break;
				case '.':
					field[i + 1][j + 1] = 0;
					break;
				case 'E':
					field[i + 1][j + 1] = 2;
					break;
				default:
					assert(false);
				}
			}
		}
		Graph g(SIZE * SIZE * SIZE*SIZE*2+1);
		int dx[] = { -1,0,1,0,0 };
		int dy[] = { 0,1,0,-1,0 };
		for (int qx = 1; qx < W + 1; ++qx) {
			for (int qy = 1; qy < H + 1; ++qy) {
				for (int ax = 1; ax < W + 1; ++ax) {
					for (int ay = 1; ay < H + 1; ++ay) {
						for (int nt = 0; nt < 2; ++nt) {
							int nowhash = gethash(qx, qy, ax, ay, nt);
							if (qx == ax&&qy == ay) {
								if (nt) {
									continue;
								}
								else {
									g[nowhash].push_back(0);
								}
							}
							if (field[qy][qx] == 2&&nt) {
								g[nowhash].push_back(0);
							}
							for (int way = 0; way < 5; ++way) {
								int nqx(qx), nqy(qy), nax(ax), nay(ay);
								if (nt) {
									nqx += dx[way]; nqy += dy[way];
									if (field[nqy][nqx]==-1) continue;
								}
								else {
									nax += dx[way]; nay += dy[way];
									if ((field[nay][nax]==-1)) continue;
								}
								if (nax == nqx&&nay == nqy) {
									if (nt) {
										continue;
									}
									else {
										g[nowhash].push_back(0);
									}
								}
								int nexthash = gethash(nqx, nqy, nax, nay, !nt);
								g[nowhash].push_back(nexthash);

							}
						}
					}
				}
			}
		}
		auto anss=solve(g);
		int ans = anss[gethash(sqx, sqy, sax, say, 1)];
		if (ans == 0)cout << "Queen can not escape and Army can not catch Queen." << endl;
		else if (ans == 1)cout << "Queen can escape." << endl;
		else cout << "Army can catch Queen." << endl;
		g.clear();
	}


	return 0;
}

