                    for (int qx = 0; qx < W; qx++) {
                        if (sss[qy][qx] == '#') { continue; }
                        for (int ay = 0; ay < H; ay++) {
                            for (int ax = 0; ax < W; ax++) {
                                if (sss[ay][ax] == '#') { continue; }
                                const int ss = encenc(a_tern, qy, qx, ay, ax);
                                for (int ad = 0; ad < 5; ad++) {
                                    const int day = dys[ad], dax = dxs[ad];
                                    const int nay = ay + day, nax = ax + dax;
                                    if (nay < 0 or nay >= H or nax < 0 or nax >= W) { continue; }
                                    if (sss[nay][nax] == '#') { continue; }
                                    const int nss = encenc(1 - a_tern, qy, qx, nay, nax);
                                    g[nss].push_back(ss);
                                    odim[ss]++;
                                }
                            }
                        }
                    }
                }
            }
        }
        std::vector<int> win(SS * 2, 2);  // 0:lose, 1:win, 2:draw
        std::queue<int> Q;
        for (int a_tern = 0; a_tern < 2; a_tern++) {
            for (int qy = 0; qy < H; qy++) {
                for (int qx = 0; qx < W; qx++) {
                    if (sss[qy][qx] == '#') { continue; }
                    for (int ay = 0; ay < H; ay++) {
                        for (int ax = 0; ax < W; ax++) {
                            if (sss[ay][ax] == '#') { continue; }
                            const int ss = encenc(a_tern, qy, qx, ay, ax);
                            if (a_tern == 0) {
                                if (qy == ay and qx == ax) {
                                    win[ss] = 0;
                                    Q.push(ss);
                                } else if (sss[qy][qx] == 'E') {
                                    win[ss] = 1;
                                    Q.push(ss);
                                }
                            } else {
                                if (qy == ay and qx == ax) {
                                    win[ss] = 1;
                                    Q.push(ss);
                                }
                            }
                        }
                    }
                }
            }
        }
        while (not Q.empty()) {
            const int ss = Q.front();
            Q.pop();
            for (const int nss : g[ss]) {
                if (win[nss] != 2) { continue; }
                odim[nss]--;
                if (win[ss] == 0) {
                    win[nss] = 1;
                    Q.push(nss);
                } else if (win[ss] == 1) {
                    if (odim[nss] == 0) {
                        win[nss] = 0;
                        Q.push(nss);
                    }
                }
            }
        }
        int qy = -1, qx = -1, ay = -1, ax = -1;
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (sss[i][j] == 'Q') { qy = i, qx = j; }
                if (sss[i][j] == 'A') { ay = i, ax = j; }
            }
        }
        const int init_ss = encenc(false, qy, qx, ay, ax);
        const int ans     = win[init_ss];
        if (ans == 0) {
            outln("Army can catch Queen.");
        } else if (ans == 1) {
            outln("Queen can escape.");
        } else {
            outln("Queen can not escape and Army can not catch Queen.");
        }
    }
    return 0;
}

#include<iostream>
#include<queue>

using namespace std;
#define REP(i,b,n) for(int i=b;i<n;i++)
#define rep(i,n)   REP(i,0,n)
#define pb         push_back

enum RES{DRAW=-1,WIN,LOSE};

class state{
public:
  bool who;//0 queen 1 army
  int q,a;
};


enum RES win[2][1000][1000];
int need[2][1000][1000];
vector<state> edge[2][1000][1000];


int dx[]={0,0,1,-1,0};
int dy[]={1,-1,0,0,0};
char m[30][30];

bool out(int y,int x,int r,int c){
  return y<0||x<0||y>=r||x>=c;
}

int md(int y,int x,int yy,int xx){
  return max(y,yy)-min(y,yy)+max(x,xx)-min(x,xx);
}

void solve(int q,int a,int r,int c){
  rep(i,2){
    rep(j,r*c){
      rep(k,r*c){
	win[i][j][k]=DRAW;
	edge[i][j][k].clear();
	need[i][j][k]=0;
      }
    }
  }

  rep(qi,r){
    rep(qj,c){
      if (m[qi][qj] == '#')continue;
      rep(ai,r){
	rep(aj,c){
	  if (m[ai][aj] == '#')continue;
	  rep(k,5){
	    int nex=qj+dx[k],ney=qi+dy[k];
	    if (out(ney,nex,r,c)||m[ney][nex] == '#')continue;

	    need[0][qi*c+qj][ai*c+aj]++;
	    edge[1][ney*c+nex][ai*c+aj].pb((state){0,qi*c+qj,ai*c+aj});
	  }
	  rep(k,5){
	    int nex=aj+dx[k],ney=ai+dy[k];
	    if (out(ney,nex,r,c)||m[ney][nex] == '#')continue;
	    need[1][qi*c+qj][ai*c+aj]++;
	    edge[0][qi*c+qj][ney*c+nex].pb((state){1,qi*c+qj,ai*c+aj});
	  }
	}
      }
    }
  }

  queue<state> Q;
  //win q
  rep(i,r){
    rep(j,c){
      if (m[i][j] == '#')continue;
      rep(ii,r){
	rep(jj,c){
	  if (m[ii][jj] == '#'||(ii==i&&jj==j))continue;
	  if (m[i][j] == 'E'){// && md(i,j,ii,jj)>1){
	    win[0][i*c+j][ii*c+jj]=WIN;
	    Q.push((state){0,i*c+j,ii*c+jj});
	  }
	  if (m[i][j] == 'E' && md(i,j,ii,jj) > 1){
	    win[1][i*c+j][ii*c+jj]=LOSE;
	    Q.push((state){1,i*c+j,ii*c+jj});
	  }
	}
      }
    }
  }


  //win a
  rep(i,r){
    rep(j,c){
      if (m[i][j] == '#')continue;
      rep(k,4){
	int nex=j+dx[k],ney=i+dy[k];
	if (out(ney,nex,r,c)||m[ney][nex]=='#'){
	  continue;
	}
	if (win[1][i*c+j][ney*c+nex] == DRAW){
	  win[1][i*c+j][ney*c+nex]=WIN;
	  Q.push((state){1,i*c+j,ney*c+nex});
	}
      }
    }
  }



  rep(i,r){
    rep(j,c){
      if (m[i][j] == '#')continue;
      if (win[0][i*c+j][i*c+j] == DRAW){
	win[0][i*c+j][i*c+j]=LOSE;
	Q.push((state){0,i*c+j,i*c+j});
      }
      if (win[1][i*c+j][i*c+j] == DRAW){
	win[1][i*c+j][i*c+j]=WIN;
	Q.push((state){1,i*c+j,i*c+j});
      }
    }
  }


  while(!Q.empty()){
    state now = Q.front();Q.pop();
    //cout << now.who <<" " << now.q <<" " << now.a <<" " << win[now.who][now.q][now.a] << endl;

    rep(i,edge[now.who][now.q][now.a].size()){
      state next=edge[now.who][now.q][now.a][i];
      need[next.who][next.q][next.a]--;
      //      if (next.who == 0 &&next.q == 0 && next.a == 1)
      //	cout <<now.who <<" " <<  now.q <<" " << now.a << endl;

      if (win[next.who][next.q][next.a] != DRAW)continue;
      if (win[now.who][now.q][now.a] == LOSE){
	win[next.who][next.q][next.a]=WIN;
	Q.push(next);
      }else if (need[next.who][next.q][next.a] == 0 && 
		win[next.who][next.q][next.a]  == DRAW){
	win[next.who][next.q][next.a]=LOSE;
	Q.push(next);
      }
    }
  }

  //  cout << need[0][q][a]<<endl;
  if (win[0][q][a] == LOSE)cout << "Army can catch Queen." << endl;
  else if (win[0][q][a] == WIN)cout << "Queen can escape." << endl;
  else cout << "Queen can not escape and Army can not catch Queen."<<endl;
  return;
}


main(){
  int r,c;
  while(cin>>c>>r && c){
    rep(i,r){
      rep(j,c)cin>>m[i][j];
    }
    int q,a;
    rep(i,r){
      rep(j,c){
	if (m[i][j] == 'Q')q=i*c+j;
	if (m[i][j] == 'A')a=i*c+j;
      }
    }
    solve(q,a,r,c);
  }
}

#include "bits/stdc++.h"
using namespace std;

typedef vector<vector<int>> Graph;

vector<int> solve(const Graph&g) {
	vector<int>nums(g.size());
	map<int, vector<int>> revg;
	for (int i = 0; i < g.size();++i){
		auto es(g[i]);
		nums[i] = es.size();
		for (auto e : es) {
			revg[e].push_back(i);
		}

	}

	//1 : win   0 : draw   -1 :loss
	vector<int>anss(g.size());
	queue<int>que;
	for (int i = 0; i < g.size(); ++i) {
		if (!nums[i]) {
			que.push(i);
			anss[i] = -1;
		}
	}
	while (!que.empty()) {
		int now(que.front());
		que.pop();
		if (anss[now] == 1) {
			for (auto reve : revg[now]) {
				nums[reve]--;
				if (!nums[reve]&&anss[reve]==0) {
					anss[reve] = -1;
					que.push(reve);
				}
			}
		}
		else if (anss[now] == -1) {
			if (revg.find(now)!=revg.end()) {
				for (auto reve : revg[now]) {
					if (anss[reve] == 0) {
						anss[reve] = 1;
						que.push(reve);
					}
				}
			}

		}
		else {
			assert(false);
		}
	}
	return anss;
}
int SIZE = 30;
int gethash(int ax, int ay, int bx, int by, int c) {
	return (ax-1)* SIZE * SIZE * SIZE * 2 + (ay-1)* SIZE * SIZE * 2 + (bx-1) * SIZE * 2 + (by-1) * 2 + c+1;
}

int main() {
	while (true) {
		int W, H; cin >> W >> H;
		if (!W)break;
		vector<vector<int>>field(H + 2, vector<int>(W + 2,-1));
		int sqx, sqy, sax, say;
		for (int i = 0; i < H; ++i) {
			string st; cin >> st;
			for (int j = 0; j < W; ++j) {
				switch (st[j]) {
				case 'Q':
					field[i + 1][j + 1] = 0;
					sqx = j + 1; sqy = i + 1;
					break;
				case 'A':
					field[i + 1][j + 1] = 0;
					sax = j + 1; say = i + 1;
					break;
				case '#':
					break;
				case '.':
					field[i + 1][j + 1] = 0;
					break;
				case 'E':
					field[i + 1][j + 1] = 2;
					break;
				default:
					assert(false);
				}
			}
		}
		SIZE = max(H, W);
		Graph g(SIZE * SIZE * SIZE*SIZE*2+1);
		int dx[] = { -1,0,1,0,0 };
		int dy[] = { 0,1,0,-1,0 };
		for (int qx = 1; qx < W + 1; ++qx) {
			for (int qy = 1; qy < H + 1; ++qy) {
				if (field[qy][qx] == -1)continue;
				for (int ax = 1; ax < W + 1; ++ax) {
					for (int ay = 1; ay < H + 1; ++ay) {
						if (field[ay][ax] == -1)continue;
						for (int nt = 0; nt < 2; ++nt) {
							int nowhash = gethash(qx, qy, ax, ay, nt);
							if (qx == ax&&qy == ay) {
								if (nt) {
									continue;
								}
								else {
									g[nowhash].push_back(0);
								}
							}
							if (field[qy][qx] == 2&&nt) {
								g[nowhash].push_back(0);
							}
							for (int way = 0; way < 5; ++way) {
								int nqx(qx), nqy(qy), nax(ax), nay(ay);
								if (nt) {
									nqx += dx[way]; nqy += dy[way];
									if (field[nqy][nqx]==-1) continue;
								}
								else {
									nax += dx[way]; nay += dy[way];
									if ((field[nay][nax]==-1)) continue;
								}
								if (nax == nqx&&nay == nqy) {
									if (nt) {
										continue;
									}
									else {
										g[nowhash].push_back(0);
									}
								}
								int nexthash = gethash(nqx, nqy, nax, nay, !nt);
								g[nowhash].push_back(nexthash);

							}
						}
					}
				}
			}
		}
		auto anss=solve(g);
		int ans = anss[gethash(sqx, sqy, sax, say, 1)];
		if (ans == 0)cout << "Queen can not escape and Army can not catch Queen." << endl;
		else if (ans == 1)cout << "Queen can escape." << endl;
		else cout << "Army can catch Queen." << endl;
	}


	return 0;
}

#include <bits/stdc++.h>
using namespace std;
#define FOR(i,k,n) for(int i = (int)(k); i < (int)(n); i++)
#define REP(i,n) FOR(i,0,n)
#define ALL(a) a.begin(), a.end()
#define MS(m,v) memset(m,v,sizeof(m))
typedef long long ll;
typedef long double ld;
typedef vector<int> vi;
typedef vector<string> vs;
typedef pair<int, int> pii;
const int MOD = 1000000007;
const int INF = MOD + 1;
const ld EPS = 1e-12;
template<class T> T &chmin(T &a, const T &b) { return a = min(a, b); }
template<class T> T &chmax(T &a, const T &b) { return a = max(a, b); }

/*--------------------template--------------------*/
const int dx[] = { -1, 0, 0, 1 }, dy[] = { 0, -1, 1, 0 };
//[const int dx[] = { -1, -1, -1, 0, 0, 1, 1, 1 }, dy[] = { 0, -1, 1, -1, 1, 0, -1, 1 };
bool valid(int x, int y, int h, int w) { return (x >= 0 && y >= 0 && x < h&&y < w); }
int place(int x, int y, int w) { return w*x + y; }

int dp[2][33][33][33][33]; //turn, x_queen, y_queen, x_army, y_army
int h, w;
vs fld;

typedef tuple<int, int, int, int, int> state;

void init()
{
	fld.clear(); fld.resize(h);
	MS(dp, 0);
}

int main()
{
	cin.sync_with_stdio(false); cout << fixed << setprecision(10);
	while (cin >> w >> h, h)
	{
		init();
		REP(i, h) cin >> fld[i];
		pii army, queen;
		set<pii> exit;
		REP(i, h)REP(j, w)
		{
			if (fld[i][j] == '#') continue;
			if (fld[i][j] == 'Q') queen = pii(i, j);
			if (fld[i][j] == 'A') army = pii(i, j);
			if (fld[i][j] == 'E') exit.emplace(i, j);
			fld[i][j] = '.';
		}
		vector<state> updates;
		for (auto i : exit)REP(ax, h)REP(ay, w)
		{
			int qx = i.first, qy = i.second;
			dp[0][qx][qy][ax][ay] = 1;
			updates.push_back(state(0, qx, qy, ax, ay));
		}
		REP(i, h)REP(j, w)
		{
			if (fld[i][j] == '#') continue;
			dp[0][i][j][i][j] = -1;
			updates.push_back(state(0, i, j, i, j));
		}
		REP(i, 2)REP(qx, h)REP(qy, w)REP(ax, h)REP(ay, w) updates.push_back(state(i, qx, qy, ax, ay));
		while (!updates.empty())
		{
			vector<state> nexts;
			for (auto tmp : updates)
			//REP(i, 2)REP(qx, h)REP(qy, w)REP(ax, h)REP(ay, w)
			{
				int i, qx, qy, ax, ay;
				tie(i, qx, qy, ax, ay) = tmp;
				if (!valid(qx, qy, h, w) || !valid(ax, ay, h, w) || dp[i][qx][qy][ax][ay] != 0) continue;
				if (i == 0)
				{
					set<int> st;
					st.insert(dp[1][qx][qy][ax][ay]);
					REP(j, 4)
					{
						int nx = qx + dx[j], ny = qy + dy[j];
						if (!valid(nx, ny, h, w) || fld[nx][ny] == '#') continue;
						st.insert(dp[1][nx][ny][ax][ay]);
					}
					if (st.size() == 1 && *st.begin() == -1)
					{
						dp[i][qx][qy][ax][ay] = -1;
					}
					else if (st.count(1))
					{
						dp[i][qx][qy][ax][ay] = 1;
					}
				}
				else
				{
					set<int> st;
					st.insert(dp[0][qx][qy][ax][ay]);
					REP(j, 4)
					{
						int nx = ax + dx[j], ny = ay + dy[j];
						if (!valid(nx, ny, h, w) || fld[nx][ny] == '#') continue;
						st.insert(dp[0][qx][qy][nx][ny]);
					}
					if (st.size() == 1 && *st.begin() == 1)
					{
						dp[i][qx][qy][ax][ay] = 1;
					}
					else if (st.count(-1))
					{
						dp[i][qx][qy][ax][ay] = -1;
					}
				}
				if (dp[i][qx][qy][ax][ay] != 0)
				{
					if (dp[1 - i][qx][qy][ax][ay] == 0)
					{
						nexts.push_back(state(1 - i, qx, qy, ax, ay));
					}
					REP(i, 2)REP(j, 4)REP(k, 4)
					{
						if (!valid(qx + dx[j], qy + dy[j], h, w) || !valid(ax + dx[k], ay + dy[k], h, w) || dp[i][qx + dx[j]][qy + dy[j]][ax + dx[k]][ay + dy[k]] != 0) continue;
						nexts.push_back(state(i, qx + dx[j], qy + dy[j], ax + dx[k], ay + dy[k]));
					}
				}
			}
			sort(ALL(updates));
			updates.erase(unique(ALL(updates)), updates.end());
			updates = nexts;
		}


		int ans = dp[0][queen.first][queen.second][army.first][army.second];
		if (ans == 1) cout << "Queen can escape." << endl;
		else if (ans == 0) cout << "Queen can not escape and Army can not catch Queen." << endl;
		else cout << "Army can catch Queen." << endl;
	}

	return 0;
}

