};
int w,h;

bool check(int y, int x) {
  return y<0||y>=h||x<0||x>=w||ba[y][x]=='#';
}

int main() {
  while(cin>>w>>h,w||h) {
    int qx,qy;
    int ax,ay;
    REP(i,h) REP(j,w) {
      cin >> ba[i][j];
      if (ba[i][j] == 'Q') qy=i,qx=j;
      else if(ba[i][j] == 'A') ay=i,ax=j;
    }
    memset(memo,-1,sizeof(memo));
    memset(num,0,sizeof(num));
    queue<P> Q;
    const int dy[] = {0,-1,0,1,0};
    const int dx[] = {0,0,1,0,-1};

    REP(y1,h) REP(x1,w) {
      if (ba[y1][x1]=='#') continue;
      REP(y2,h) REP(x2,w) {
        if (ba[y2][x2]=='#') continue;
        REP(k,2) {
          if (y1==y2&&x1==x2) {
            // queen lose
            memo[y1][x1][y2][x2][k] = k;
            Q.push(P(y1,x1,y2,x2,k));
          } else if (ba[y1][x1]=='E' && k==0) {
            // queen win
            memo[y1][x1][y2][x2][k] = 1;
            Q.push(P(y1,x1,y2,x2,k));
          }

          REP(d,5) {
            int yy1=y1+(k?dy[d]:0);
            int xx1=x1+(k?dx[d]:0);
            int yy2=y2+(k?0:dy[d]);
            int xx2=x2+(k?0:dx[d]);
            if (check(yy1,xx1)||check(yy2,xx2)) continue;
            num[yy1][xx1][yy2][xx2][!k]++;
          }
        }
      }
    }
    //cout << num[0][2][0][0][1] << endl;
    while(!Q.empty()) {
      P p = Q.front(); Q.pop();

      int y1=p.qy,x1=p.qx,y2=p.ay,x2=p.ax,k=p.t;
      //printf("(%d,%d),(%d,%d),%d  %d\n",y1,x1,y2,x2,k,memo[y1][x1][y2][x2][k]);
      REP(d,5) {
        int yy1=y1+(k?dy[d]:0);
        int xx1=x1+(k?dx[d]:0);
        int yy2=y2+(k?0:dy[d]);
        int xx2=x2+(k?0:dx[d]);
        if (check(yy1,xx1)||check(yy2,xx2)) continue;        
        if (memo[yy1][xx1][yy2][xx2][!k]!=-1) continue;
        if (memo[y1][x1][y2][x2][k] == 0) {
          memo[yy1][xx1][yy2][xx2][!k] = 1;
          Q.push(P(yy1,xx1,yy2,xx2,!k));
        } else {
          num[yy1][xx1][yy2][xx2][!k]--;
          if (num[yy1][xx1][yy2][xx2][!k] == 0) {
            //printf("  (%d,%d),(%d,%d),%d  \n",yy1,xx1,yy2,xx2,!k);
            memo[yy1][xx1][yy2][xx2][!k] = 0;
            Q.push(P(yy1,xx1,yy2,xx2,!k));
          }
        }
      }
    }
    if (memo[qy][qx][ay][ax][0] == 0) {
      cout << "Army can catch Queen." << endl;
    } else if (memo[qy][qx][ay][ax][0] == 1) {
      cout << "Queen can escape." << endl;
    } else {
      cout << "Queen can not escape and Army can not catch Queen." << endl;
    }
  }
}

