using namespace std;
const int nmax = 30 + 1;
const int dx[5] = {0, 0, 1, -1, 0};
const int dy[5] = {1, -1, 0, 0, 0};

struct state {
	int x1, y1, x2, y2;
	bool turn; // 0 for Q ed
	state() {};
	state(int _x1, int _y1, int _x2, int _y2, bool _turn) {
		x1 = _x1, x2 = _x2, y1 = _y1, y2 = _y2, turn = _turn;
	}
};

bool f[nmax][nmax][nmax][nmax][2]; // 0 for lose
vector<state> Q;
bool ed[nmax][nmax][nmax][nmax][2];
char a[nmax][nmax];
int n, m;

int check(state &s)
{
	int x1, x2, y1, y2;
	int nxtt = s.turn ^ 1;
	bool all = 1, exist = 0;
	if (s.turn == 0) {
		x1 = s.x1, y1 = s.y1;
		for (int j = 0; j < 5; ++j) {
			x2 = s.x2 + dx[j], y2 = s.y2 + dy[j];
			if (x2 <= 0 || x2 > n || y2 <= 0 || y2 > m || a[x2][y2] == '#') continue;
			if (!ed[x1][y1][x2][y2][nxtt]) {
				all = 0;
				continue;
			}
			if (f[x1][y1][x2][y2][nxtt] == 0)
				exist = 1, all = 0;
		}
	}
	else {
		for (int i = 0; i < 5; ++i) {
			x1 = s.x1 + dx[i], y1 = s.y1 + dy[i];
			if (x1 <= 0 || x1 > n || y1 <= 0 || y1 > m || a[x1][y1] == '#') continue;
			x2 = s.x2, y2 = s.y2;
			if (!ed[x1][y1][x2][y2][nxtt]) {
				all = 0;
				continue;
			}
			if (f[x1][y1][x2][y2][nxtt] == 1)
				exist = 1, all = 0;
		}
	}
	if (all)
		return nxtt;
	else if (exist)
		return s.turn;
	else return 2;
}

int main()
{
	while (1) {
		scanf("%d%d", &n, &m);
		swap(n, m);
		if (n == 0 && m == 0) break;
		for (int i = 1; i <= n; ++i)
			scanf("%s", a[i] + 1);
		memset(ed, 0, sizeof(ed));
		Q.clear();
		for (int i = 1; i <= n; ++i)
			for (int j = 1; j <= m; ++j) if (a[i][j] != '#')
				for (int k = 1; k <= n; ++k)
					for (int l = 1; l <= m; ++l) if (a[k][l] != '#') {
						if (i == k && j == l) {
							state tmp(i, j, k, l, 1);
							Q.push_back(tmp);
							f[i][j][k][l][1] = 0;
							ed[i][j][k][l][1] = 1;
						}
						else if (a[i][j] == 'E') {
							state tmp(i, j, k, l, 1);
							Q.push_back(tmp);
							f[i][j][k][l][1] = 1;
							ed[i][j][k][l][1] = 1;
						}
					}
		for (int tmpi = 0; tmpi < Q.size(); ++tmpi) {
			state *now = &Q[tmpi];
			int x1, x2, y1, y2, tmpf;
//			printf("(%d) (%d) %d: %d\n", now->y1, now->y2, now->turn, f[now->x1][now->y1][now->x2][now->y2][now->turn]);
//			printf("(%d, %d) (%d, %d) %d: %d\n", now->x1, now->y1, now->x2, now->y2, now->turn, f[now->x1][now->y1][now->x2][now->y2][now->turn]);
			bool nxtt = now->turn ^ 1;
			if (now->turn == 1) {
				x1 = now->x1, y1 = now->y1;
				for (int j = 0; j < 5; ++j) {
					x2 = now->x2 + dx[j], y2 = now->y2 + dy[j];
					if (x2 <= 0 || x2 > n || y2 <= 0 || y2 > m || a[x2][y2] == '#') continue;
					if (ed[x1][y1][x2][y2][nxtt]) continue;
					state tmp(x1, y1, x2, y2, nxtt);
					if ((tmpf = check(tmp)) != 2) {
						ed[x1][y1][x2][y2][nxtt] = 1;
						f[x1][y1][x2][y2][nxtt] = tmpf;
						Q.push_back(tmp);
					}
				}
			}
			else {
				for (int i = 0; i < 5; ++i) {
					x1 = now->x1 + dx[i], y1 = now->y1 + dy[i];
					if (x1 <= 0 || x1 > n || y1 <= 0 || y1 > m || a[x1][y1] == '#') continue;
					x2 = now->x2, y2 = now->y2;
					if (ed[x1][y1][x2][y2][nxtt]) continue;
					state tmp(x1, y1, x2, y2, nxtt);
					if ((tmpf = check(tmp)) != 2) {
						ed[x1][y1][x2][y2][nxtt] = 1;
						f[x1][y1][x2][y2][nxtt] = tmpf;
						Q.push_back(tmp);
					}
				}
			}
		}

		int lx, ly, rx, ry;
		for (int i = 1; i <= n; ++i)
			for (int j = 1; j <= m; ++j) {
				if (a[i][j] == 'Q')
					lx = i, ly = j;
				if (a[i][j] == 'A')
					rx = i, ry = j;
			}
		if (!ed[lx][ly][rx][ry][1])
			printf("Queen can not escape and Army can not catch Queen.\n"); // tie
		else
			if (f[lx][ly][rx][ry][1])
				printf("Queen can escape.\n"); //win
			else
				printf("Army can catch Queen.\n"); //lose
	}
	return 0;
}

#include <bits/stdc++.h>
using namespace std;
#define FOR(i,k,n) for(int i = (int)(k); i < (int)(n); i++)
#define REP(i,n) FOR(i,0,n)
#define ALL(a) a.begin(), a.end()
#define MS(m,v) memset(m,v,sizeof(m))
typedef long long ll;
typedef long double ld;
typedef vector<int> vi;
typedef vector<string> vs;
typedef pair<int, int> pii;
const int MOD = 1000000007;
const int INF = MOD + 1;
const ld EPS = 1e-12;
template<class T> T &chmin(T &a, const T &b) { return a = min(a, b); }
template<class T> T &chmax(T &a, const T &b) { return a = max(a, b); }

/*--------------------template--------------------*/
const int dx[] = { -1, 0, 0, 1 }, dy[] = { 0, -1, 1, 0 };
//[const int dx[] = { -1, -1, -1, 0, 0, 1, 1, 1 }, dy[] = { 0, -1, 1, -1, 1, 0, -1, 1 };
bool valid(int x, int y, int h, int w) { return (x >= 0 && y >= 0 && x < h&&y < w); }
int place(int x, int y, int w) { return w*x + y; }

int dp[2][33][33][33][33]; //turn, x_queen, y_queen, x_army, y_army
int h, w;
vs fld;

struct state
{
	int turn, qx, qy, ax, ay;
	state(int a, int b, int c, int d, int e) :turn(a), qx(b), qy(c), ax(d), ay(e) {};
};

void init()
{
	fld.clear(); fld.resize(h);
	MS(dp, 0);
}

int main()
{
	cin.sync_with_stdio(false); cout << fixed << setprecision(10);
	while (cin >> w >> h, h)
	{
		init();
		REP(i, h) cin >> fld[i];
		pii army, queen;
		set<pii> exit;
		REP(i, h)REP(j, w)
		{
			if (fld[i][j] == '#') continue;
			if (fld[i][j] == 'Q') queen = pii(i, j);
			if (fld[i][j] == 'A') army = pii(i, j);
			if (fld[i][j] == 'E') exit.emplace(i, j);
			fld[i][j] = '.';
		}
		for (auto i : exit)REP(ax, h)REP(ay, w)
		{
			int qx = i.first, qy = i.second;
			dp[0][qx][qy][ax][ay] = 1;
		}
		REP(i, h)REP(j, w)
		{
			if (fld[i][j] == '#') continue;
			dp[0][i][j][i][j] = -1;
		}
		vector<state> updates;
		REP(i, 2)REP(qx, h)REP(qy, w)REP(ax, h)REP(ay, w) updates.push_back(state(i, qx, qy, ax, ay));
		while (!updates.empty())
		{
			vector<state> nexts;
			for (auto tmp : updates)
			{
				REP(k, 4)REP(l, 4)REP(m, 2)
				{
					bool update = false;
					int i = m, qx = tmp.qx + dx[k], qy = tmp.qy + dy[k], ax = tmp.ax + dx[l], ay = tmp.ay + dy[l];
					if (dp[i][qx][qy][ax][ay] != 0 || !valid(qx, qy, h, w) || !valid(ax, ay, h, w)) continue;
					if (i == 0)
					{
						set<int> st;
						st.insert(dp[1][qx][qy][ax][ay]);
						REP(j, 4)
						{
							int nx = qx + dx[j], ny = qy + dy[j];
							if (!valid(nx, ny, h, w) || fld[nx][ny] == '#') continue;
							st.insert(dp[1][nx][ny][ax][ay]);
						}
						if (st.size() == 1 && *st.begin() == -1)
						{
							dp[0][qx][qy][ax][ay] = -1;
							update = true;
						}
						else if (st.count(1))
						{
							dp[0][qx][qy][ax][ay] = 1;
							update = true;
						}
					}
					else
					{
						set<int> st;
						st.insert(dp[0][qx][qy][ax][ay]);
						REP(j, 4)
						{
							int nx = ax + dx[j], ny = ay + dy[j];
							if (!valid(nx, ny, h, w) || fld[nx][ny] == '#') continue;
							st.insert(dp[0][qx][qy][nx][ny]);
						}
						if (st.size() == 1 && *st.begin() == 1)
						{
							dp[1][qx][qy][ax][ay] = 1;
							update = true;
						}
						else if (st.count(-1))
						{
							dp[1][qx][qy][ax][ay] = -1;
							update = true;
						}
					}
					if (update) nexts.push_back(state(i, qx, qy, ax, ay));
				}
			}
			updates = nexts;
		}

		int ans = dp[0][queen.first][queen.second][army.first][army.second];
		if (ans == 1) cout << "Queen can escape." << endl;
		else if (ans == 0) cout << "Queen can not escape and Army can not catch Queen." << endl;
		else cout << "Army can catch Queen." << endl;
	}

	return 0;
}

