bool cg(int qx, int qy, int ax, int ay)
{
	if(qx==ax&&qy==ay) return false;
	bool hs[30][30]={0};

	queue<State> q;
	q.push(State(qx,qy,0));
	hs[qx][qy]=1;

	while(!q.empty())
	{
		State s=q.front(); q.pop();
		if(f[s.x][s.y]=='E') return true;

		for(int i=0; i<4; i++)
		{
			int tx=s.x+dx[i], ty=s.y+dy[i];
			if(tx<0||ty<0||tx>=W||ty>=H) continue;
			if(f[tx][ty]=='#') continue;
			if(tx==ax&&ty==ay) continue;
			if(hs[tx][ty]) continue;

			hs[tx][ty]=1;

			q.push(State(tx,ty,s.c+1));
		}
	}

	return false;
}

int judge(int qx, int qy, int ax, int ay)
{	
