	for(int k=0; k<gN; k++)
	{
		if(dist[qx][qy][gx[k]][gy[k]] < dist[ax][ay][gx[k]][gy[k]]) return 1;
	}
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		if(dist[qx][qy][j][i]==-1) continue;
		if(dist[qx][qy][j][i]==dist[ax][ay][j][i]) cnt++;

	}
	if(!cg(qx,qy,ax,ay)) return -1;
	if(cnt>=2) return 0;
	return -1;
}

void Search()
{
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		for(int k=0; k<H; k++)
		for(int l=0; l<W; l++)
		{
			if(f[l][k]=='#') continue;
			relt[j][i][l][k]=judge(j,i,l,k);
		}
	}
}

