	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(dist[a][b][j][i]>=dist[x][y][j][i]) continue;
		if(f[j][i]=='#') continue;

		if(!ck[j][i][x][y]) memo[j][i][x][y]=Aphase(j,i,x,y,dist[a][b][j][i]);
		memo[a][b][x][y]=max(memo[a][b][x][y], memo[j][i][x][y]);
	}

	return memo[a][b][x][y];
}

int main()
{
	while(cin >> W >> H, (W||H))
	{
		int ax,ay,qx,qy;
		gN=0;
		memset(ck,0,sizeof(ck));
		for(int i=0; i<H; i++)
		for(int j=0; j<W; j++)
		{
			cin >> f[j][i];
			if(f[j][i]=='A')
			{
				ax=j; ay=i;
			}
			if(f[j][i]=='Q')
			{
				qx=j; qy=i;
			}
			if(f[j][i]=='E')
			{
				gx[gN]=j;
				gy[gN]=i;
				gN++;
			}
		}

		calcD();
		Search();

		int ret=Qphase(qx,qy,ax,ay);
		if(ret==-1) 	cout << "Army can catch Queen." << endl;
		else if(ret==0)	cout << "Queen can not escape and Army can not catch Queen." << endl;
		else			cout << "Queen can escape." << endl;
	}
}

