#include <iostream>
#include <queue>
#include <cstring>
#include <algorithm>
using namespace std;

int dx[]={-1,0,1,0};
int dy[]={0,-1,0,1};

bool ck[30][30][30][30]; // ax,ay qx,qy
int dist[30][30][30][30]; // xy to x'y'
int relt[30][30][30][30];
int memo[30][30][30][30];
int W,H,gx[900],gy[900],gN;
char f[31][31];

class State
{
public:
	int x,y,c;
	State(int x, int y, int c)
	:x(x),y(y),c(c)
	{}
};

void bfs(int x, int y)
{
	bool hs[30][30]={0};

	queue<State> q;
	q.push(State(x,y,0));
	hs[x][y]=1;

	while(!q.empty())
	{
		State s=q.front(); q.pop();
		dist[x][y][s.x][s.y]=s.c;

		for(int i=0; i<4; i++)
		{
			int tx=s.x+dx[i], ty=s.y+dy[i];
			if(tx<0||ty<0||tx>=W||ty>=H) continue;
			if(f[tx][ty]=='#') continue;
			if(hs[tx][ty]) continue;

			hs[tx][ty]=1;

			q.push(State(tx,ty,s.c+1));
		}
	}
}

void calcD()
{
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		bfs(j,i);
	}
}

int judge(int qx, int qy, int ax, int ay)
{
	int cnt=0;
	for(int k=0; k<gN; k++)
	{
		if(dist[qx][qy][gx[k]][gy[k]] < dist[ax][ay][gx[k]][gy[k]]) return 1;

		if(dist[qx][qy][gx[k]][gy[k]] == dist[ax][ay][gx[k]][gy[k]]) cnt++;
	}
	if(cnt>=2) return 0;
	return -1;
}

void Search()
{
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		for(int k=0; k<H; k++)
		for(int l=0; l<W; l++)
		{

			if(f[l][k]=='#') continue;
			relt[j][i][l][k]=judge(j,i,l,k);
		}
	}
}

int Aphase(int a, int b, int x, int y);
int Qphase(int a, int b, int x, int y);

int Aphase(int a, int b, int x, int y, int d)
{
	int ret=relt[a][b][x][y];
	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(f[j][i]=='#') continue;
		if(dist[x][y][j][i]<=d)
		{
			bool g=true;
			for(int k=0; k<gN; k++)
			{
				if(dist[a][b][gx[k]][gy[k]] < dist[j][i][gx[k]][gy[k]])
				{
					g=false;
					break;
				}
			}
			if(!g) continue;

			ret=min(ret, Qphase(a,b,j,i));

		}
	}
	return ret;
}

int Qphase(int a, int b, int x, int y)
{
	if(ck[a][b][x][y]) return memo[a][b][x][y];
	ck[a][b][x][y]=1;
	memo[a][b][x][y]=relt[a][b][x][y];

	for(int i=0; i<H; i++)
	for(int j=0; j<W; j++)
	{
		if(j==a&&i==b) continue;
		if(dist[a][b][j][i]>=dist[x][y][j][i]) continue;
		if(f[j][i]=='#') continue;

		if(ck[j][i][x][y]) memo[a][b][x][y]=max(memo[a][b][x][y], memo[j][i][x][y]);
		else memo[a][b][x][y]=max(memo[a][b][x][y], Aphase(j,i,x,y,dist[a][b][j][i]));
	}

	return memo[a][b][x][y];
}

int main()
{
	while(cin >> W >> H, (W||H))
	{
		int ax,ay,qx,qy;
		gN=0;
		memset(ck,0,sizeof(ck));
		for(int i=0; i<H; i++)
		for(int j=0; j<W; j++)
		{
			cin >> f[j][i];
			if(f[j][i]=='A')
			{
				ax=j; ay=i;
			}
			if(f[j][i]=='Q')
			{
				qx=j; qy=i;
			}
			if(f[j][i]=='E')
			{
				gx[gN]=j;
				gy[gN]=i;
				gN++;
			}
		}

		calcD();
		Search();

		int ret=Qphase(qx,qy,ax,ay);
		if(ret==-1) 	cout << "Army can catch Queen." << endl;
		else if(ret==0)	cout << "Queen can not escape and Army can not catch Queen." << endl;
		else			cout << "Queen can escape." << endl;
	}
}

