  int dy[5] = { 0, 0, 1, -1, 0 };
  queue<State> que;
  for (int y = 1; y <= height; y++) {
    for (int x = 1; x <= width; x++) {
      if (field[y][x] == 'Q') {
        qstartx = x;
        qstarty = y;
      } else if (field[y][x] == 'A')  {
        astartx = x;
        astarty = y;
      } else if (field[y][x] == 'E') {
