#include <bits/stdc++.h>
#define N 86400
#define double long double
#define int long long
using namespace std;
const int INF = 1LL<<55;
const double EPS = 1e-8;
int n;
double vol;
int A[N];

bool check(double x){
  double mn1 = INF,mn2 = INF;
  double tank = vol;
  for(int i=0;i<N;i++){
    tank -= A[i%N];
    tank=min(vol,tank+x);

    mn1 = min(mn1,tank);
  }

  for(int i=0;i<N;i++){
    tank -= A[i%N];
    tank=min(vol,tank+x);
    mn2 = min(mn2,tank);
  }

  if(mn1 < 0) return 0;  
  return mn1  - mn2 < 0;
}

double search(){
  double L=0,M,R=(1e6)+10,cnt=50;
  while(cnt--){
    M = (L+R)/2;
    if(check(M)) R = M;
    else L = M;
  }
  return L;
}

signed main(){
  while(1){
  cin>>n>>vol;
  if(!n&&vol==0)break;
  memset(A,0,sizeof(A));  
  for(int i=0,s,t,u;i<n;i++){
    cin>>s>>t>>u;
    while(s < t) A[s++] = u;
  }
  printf("%.8Lf\n",search());
  }  
  return 0;
}

