int n,l;

bool ok(double m,int s[],int t[],int u[]){
  int rest=l;
  rest+=m*s[0];
  if(rest>l) rest=l;
  for(int i=1;i<n;i++){
    rest+=m*(s[i]-t[i-1]);
    if(rest>l) rest=l;
    rest=rest+(m-u[i])*(t[i]-s[i]);
    if(rest>l) rest=l;
    if(rest<0){
      return false;
    }
  }
  int rest2=rest;
  rest2+=m*s[0];
  if(rest2>l) rest2=l;
  for(int i=0;i<n;i++){
    rest2+=m*(s[i]-t[i-1]);
    if(rest>l) rest=l;
    rest=rest+(m-u[i])*(t[i]-s[i]);
    if(rest>l) rest=l;
    if(rest<0){
      return false;
    }
  }
  if(rest2<rest){
    return false;
  }
  return true;
}

