void solve(int n,long double l){
    vector<long double> s(n),t(n),u(n);
    rep(i,0,n) cin >> s[i] >> t[i] >> u[i];
    {
        vector<long double> s_,t_,u_;
        s_.push_back(0);
        t_.push_back(s.front());
        u_.push_back(0);
        s_.push_back(s.front());
        t_.push_back(t.front());
        u_.push_back(u.front());
        rep(i,1,s.size()){
            if(s[i]>t[i-1]){
                s_.push_back(t[i-1]);
                t_.push_back(s[i]);
                u_.push_back(0);
            }
            s_.push_back(s[i]);
            t_.push_back(t[i]);
            u_.push_back(u[i]);
        }
        s_.push_back(t.back());
        t_.push_back(86400);
        u_.push_back(0);
        swap(s,s_);
        swap(t,t_);
        swap(u,u_);
    }

    auto simulation=[&](long double x,long double initial_value){
        vector<long double> res;
        long double k=initial_value;
        res.push_back(k);
        rep(i,0,s.size()){
            k+=(x-u[i])*(t[i]-s[i]);
            k=min(k,l);
            res.push_back(k);
        }
        return res;
    };

    auto ok=[&](long double x){
        auto res1=simulation(x,l);
        auto res2=simulation(x,res1.back());
        rep(i,0,res1.size()) if(res1[i]<0) return false;
        rep(i,0,res2.size()) if(res2[i]<0) return false;
        rep(i,0,res1.size()) if(res1[i]<=res2[i]) return true;
        return false;
    };

    long double lb=0,ub=inf64;
    rep(i,0,128){
        auto mid=(lb+ub)/2;
        if(ok(mid)) ub=mid;
        else lb=mid;
    }
    cout << ub << endl;
}

