#include <bits/stdc++.h>

using namespace std;

#define INF 1e9
#define EPS 1e-10
#define equal(a,b) (fabs(a-b) < EPS)
#define lt(a,b) (a-b < -EPS)
#define le(a,b) (lt(a,b) || equal(a,b))
#define MAX_N 86401

struct P{
  double s,t,u;
  P(){}
  P(double s,double t,double u) : s(s),t(t),u(u) {}
  bool operator < (const P &p)const{
    return s < p.s;
  }
};

double add(double a,double b){
  if(abs(a+b) < EPS * (abs(a)+abs(b))){
    return 0;
  }
  return a+b;
}

int main(){
  int N;
  double L;
  P p[MAX_N];
  while(cin >> N >> L,N){
    double ml = INF,mr = 0;
    for(int i = 0 ; i < N ; i++){
      cin >> p[i].s >> p[i].t >> p[i].u;
      ml = min(ml,p[i].s);
      mr = max(mr,p[i].t);
    }
    sort(p,p+N);
    double l = 0, r = L;
    for(int i = 0 ; i < 100 ; i++){
      double mid = (l + r) / 2;
      if(lt(r-l,EPS)){ break; }
      bool ok = true;
      double rem = add(L,mid*(mr-ml+1));
      for(int j = 0 ; j < N ; j++){
	double d = p[j].t-p[j].s+1;
	rem = add(rem,-(d*p[j].u));
	if(le(rem,0)){
	  ok = false;
	  break;
	}
      }
      if(ok){ r = mid; }else{ l = mid; }
    }
    printf("%.10f\n",r);
  }
  return 0;
}

