#include <bits/stdc++.h>
using namespace std;

using ld = long double;

constexpr ld eps = 1e-8;

bool check(ld m, vector<ld>& s, vector<ld>& t, vector<ld>& u, ld L) {
    const int N = s.size();
    ld first = L;
    for(int i=0; i<N; ++i) {
        first -= (t[i] - s[i]) * (u[i] - m);
        if(first <= 0) {
            return false;
        }
        if(i != N-1) {
            first += (s[i+1] - t[i]) * m;
        } else {
            first += (86400 - t[i]) * m;
        }
        first = min(first, L);
    }
    ld second = min(first + s[0] * m, L);
    for(int i=0; i<N; ++i) {
        second -= (t[i] - s[i]) * (u[i] - m);
        if(second <= 0) {
            return false;
        }
        if(i != N-1) {
            second += (s[i+1] - t[i]) * m;
        } else {
            second += (86400 - t[i]) * m;
        }
        second = min(second, L);
    }
    return second - first >= 0;
}

int main() {
    int N, L;
    while(cin >> N >> L, N) {
        vector<ld> s(N), t(N), u(N);
        for(int i=0; i<N; ++i) {
            cin >> s[i] >> t[i] >> u[i];
        }
        ld lb = 0, ub = 1e12;
        while(ub - lb > eps) {
            ld m = (ub + lb) / 2;
            if(check(m, s, t, u, L)) {
                ub = m;
            } else {
                lb = m;
            }
        }
        cout << fixed << setprecision(10) << lb << endl;
    }
}

