void solve(int n,int64_t l){
    vector<int64_t> s(n),t(n),u(n);
    rep(i,0,n) cin >> s[i] >> t[i] >> u[i];
    {
        vector<int64_t> s_,t_,u_;
        s_.push_back(0);
        t_.push_back(s.front());
        u_.push_back(0);
        s_.push_back(s.front());
        t_.push_back(t.front());
        u_.push_back(u.front());
        rep(i,1,s.size()){
            if(s[i]>t[i-1]){
                s_.push_back(t[i-1]);
                t_.push_back(s[i]);
                u_.push_back(0);
            }
            s_.push_back(s[i]);
            t_.push_back(t[i]);
            u_.push_back(u[i]);
        }
        s_.push_back(t.back());
        t_.push_back(86400);
        u_.push_back(0);
        swap(s,s_);
        swap(t,t_);
        swap(u,u_);
    }
    while(u.front()==0){
        auto x=s.front(),y=t.front(),tmp=t.back();
        rotate(s.begin(),s.begin()+1,s.end());
        rotate(t.begin(),t.begin()+1,t.end());
        rotate(u.begin(),u.begin()+1,u.end());
        for_each(s.begin(),s.end()-1,[&](int64_t &e){e-=x;});
        for_each(t.begin(),t.end()-1,[&](int64_t &e){e-=x;});
        s.back()+=tmp;
        t.back()+=tmp;
    }

    long double ans=0,maxi=-inf;
    int64_t p=0;
    rep(i,0,s.size()){
        p+=(t[i]-s[i])*u[i];
        maxi=max(maxi,u[i]-(long double)(l)/(t[i]-s[i]));
        auto ok=[&](long double x){
            if(i<s.size()-1) return x>=((long double)(p)-l)/t[i] and x>=maxi;
            long double k=l;
            rep(j,0,s.size()){
                k+=(t[j]-s[j])*(x-u[j]);
                k=min(k,(long double)(l));
                if(k+eps<0) return false;
            }
            return abs(k-l)<eps;
        };
        long double lb=ans,ub=inf;
        rep(j,0,64){
            long double mid=(lb+ub)/2;
            if(ok(mid)) ub=mid;
            else lb=mid;
        }
        ans=ub;
    }
    {
        long double x=ans,k=l;
        rep(j,0,s.size()){
            k+=(t[j]-s[j])*(x-u[j]);
            k=min(k,(long double)(l));
            assert(k>=0);
        }
        assert(abs(k-l)<eps);
    }
    cout << ans << endl;
}

