using ld=long double;
int LOOP=100;
const int DAY=86400;
ld solve(int n,ld l){
    vector<ld> sum(DAY,0);
    for(int i=0;i<n;i++){
        int s,t,u;
        cin>>s>>t>>u;
        sum[s]+=u;
        if(t<DAY) sum[t]-=u;
    }
    for(int i=0;i+1<DAY;i++){
        sum[i+1]+=sum[i];
    }
    ld lb=0,ub=1e6;
    for(int i=0;i<LOOP;i++){
        ld mid=(lb+ub)/2;
        ld rest=l;
        bool isok=true;
        for(int i=0;i<DAY;i++){
            rest=min(rest-sum[i]+mid,l);
            if(rest<1e-9){
                isok=false;
                break;
            }
        }
        ld prerest=rest;
        for(int i=0;i<DAY;i++){
            rest=min(rest-sum[i]+mid,l);
            if(rest<1e-9){
                isok=false;
                break;
            }
        }
        if(abs(rest-prerest)<1e-9 && isok) ub=mid;
        else lb=mid;
    }
    return ub;
}
