	double mid=(ub+lb)/2;
	if(check2(x,mid)<0) lb=mid;
	else ub=mid;
      }
      return check2(x,ub)>=ub;
    };
    double lb=0,ub=*max_element(u.begin(),u.end());
    for(Int i=0;i<50;i++){
