int N;
double L;

double X[86400];

bool ok(double M) {
  double hikaku[2];
  double now = L;
  for (int j=0;j<2;j++) {
      for (int i=0;i<86400;i++) {
	now = now - X[i] + M;
	if (now > L) {
	  now = L;
	} else if (now <= 0) {
	  return false;
	}
      }
      hikaku[j] = now;
  }
  if (hikaku[0] <= hikaku[1]) {
    return true;
  } else {
    return false;
  }
}

int main() {
  while(scanf("%d%lf",&N,&L) && N) {
    for (int i=0;i<86400;i++) {
      X[i] = 0;
    }
    for (int i=0;i<N;i++) {
      int s,t;
      double u;
      scanf("%d%d%lf",&s,&t,&u);
      for (int i=s;i<t;i++) {
	X[i] = u;
      }
    }
    double lo = 0, hi = 1e6;
    while (hi - lo >1e-6) {
      double m = (hi + lo)/2.0;
      if (ok(m)) {
	hi = m;
      } else {
	lo = m;
      }
    }
    printf("%.10f\n",lo);
  }
}

#include <cstdlib>
#include <iostream>
#include <vector>
using namespace std;

constexpr int MAX_T = 86400;

bool ok(const vector<int> &s, const vector<int> &t, const vector<int> &u, int limit, double power) {
	const int n = s.size();

	bool use = false;
	int idx = 0;
	double tank = 0.0;

	for(int T = 0; T <= MAX_T; ++T) {
		if(use && T == t[idx]) {
			if(++idx == n) return true;
		}

		if(!use && T == s[idx]) {
			use = true;
		}


		if(use) {
			tank = min<double>(tank + power - u[idx], limit);
			if(tank < 0) return false;
		}
		else {
			tank = min<double>(tank + power, limit);
		}
	}

	return true;
}

int main() {
	cin.tie(nullptr);
	ios::sync_with_stdio(false);
	cout.precision(10);
	cout.setf(ios::fixed);

	for(int n, l; cin >> n >> l && n;) {
		vector<int> s(n), t(n), u(n);
		for(int i = 0; i < n; ++i) {
			cin >> s[i] >> t[i] >> u[i];
		}

		double low = 0.0, high = l;
		for(int i = 0; i < 100; ++i) {
			const double middle = (low + high) / 2.0;
			if(ok(s, t, u, l, middle)) {
				high = middle;
			}
			else {
				low = middle;
			}
		}

		cout << low << endl;
	}

	return EXIT_SUCCESS;
}

#include <cstdlib>
#include <iostream>
#include <vector>
using namespace std;

constexpr double EPS = 1e-8;
constexpr int MAX_T = 86400;

bool ok(const vector<int> &s, const vector<int> &t, const vector<int> &u, int limit, double power) {
	const int n = s.size();

	bool use = false;
	int idx = 0;
	double tank = limit;

	for(int T = 0; T <= MAX_T; ++T) {
		if(use && T == t[idx]) {
			if(++idx == n) {
				tank = min<double>(tank + (MAX_T - T) * power, limit);
				break;
			}
		}

		if(!use && T == s[idx]) {
			use = true;
		}

		if(use) {
			tank = min<double>(tank + power - u[idx], limit);
			if(tank < -EPS) return false;
		}
		else {
			tank = min<double>(tank + power, limit);
		}
	}

	return s[0] * power + tank + EPS > limit;
}

int main() {
	cin.tie(nullptr);
	ios::sync_with_stdio(false);
	cout.precision(10);
	cout.setf(ios::fixed);

	for(int n, l; cin >> n >> l && n;) {
		vector<int> s(n), t(n), u(n);
		for(int i = 0; i < n; ++i) {
			cin >> s[i] >> t[i] >> u[i];
		}

		double low = 0.0, high = 1e6;
		for(int i = 0; i < 50; ++i) {
			const double middle = (low + high) / 2.0;
			if(ok(s, t, u, l, middle)) {
				high = middle;
			}
			else {
				low = middle;
			}
		}

		cout << low << endl;
	}

	return EXIT_SUCCESS;
}

#include <iostream>
using namespace std;

const int MAX_SIZE = 86400;

bool ok(int N, int L, int s[], int t[], int u[], double m) {
  double w = L, e[2];
  for (int j = 0; j < 2; ++j) {
    for (int i = -1; i < N + 1; ++i) {
      if (i < 0) {
        w += m * s[0];
      } else if (i == N) {
        w += m * (86400 - t[N - 1]);
      } else {
        w += (m - u[i]) * (t[i] - s[i]);
      }
      if (w <= 0.0) {
        return false;
      }
      if (w > L) w = L;
    }
    e[j] = w;
  }
  return e[1] >= e[0];
}

double calc(int N, int L, int s[], int t[], int u[]) {
  double l = 0.0, r = 1e6, m;
  for (int k = 0; k < 50; ++k) {
    if (r - l < 1e-7) break;
    m = (l + r) / 2;
    if (ok(N, L, s, t, u, m)) {
      r = m;
    } else {
      l = m;
    }
  }
  return m;
}

int main() {
  int N, L, s[MAX_SIZE], t[MAX_SIZE], u[MAX_SIZE];
  while (true) {
    cin >> N >> L;
    if (!N) break;
    for (int i = 0; i < N; ++i) {
      cin >> s[i] >> t[i] >> u[i];
    }
    cout << calc(N, L, s, t, u) << endl;
  }
}

