#define EPS 0.000000001
using namespace std;


#define NUM 86400

struct Info{
	int start,end;
	double per_amount;
};

int N;
double L;
Info info[NUM];


bool check(double add_water){

	double Tank = L;

	for(int i = 0; i < N; i++){
		Tank -= (info[i].per_amount-add_water)*(double)(info[i].end-info[i].start);
		Tank = min(L,Tank);
		if(Tank <= 0.0)return false;

		if(i < N-1){
			Tank += add_water*(info[i+1].start-info[i].end);
			Tank = min(Tank,L);
		}
	}

	Tank += add_water*(86400.0-(double)(info[N-1].end));
	Tank = min(L,Tank);

	double first_day = Tank;

	Tank += add_water*info[0].start;
	Tank = min(L,Tank);

	for(int i = 0; i < N; i++){
		Tank -= (info[i].per_amount-add_water)*(double)(info[i].end-info[i].start);
		Tank = min(L,Tank);
		if(Tank <= 0.0)return false;

		if(i < N-1){
			Tank += add_water*(info[i+1].start-info[i].end);
			Tank = min(Tank,L);
		}
	}

	Tank += add_water*(86400.0-(double)(info[N-1].end));
	Tank = min(L,Tank);

	return Tank >= first_day;

}


void func(){

	for(int i = 0; i < N; i++){
		scanf("%d %d %lf",&info[i].start,&info[i].end,&info[i].per_amount);
	}

	double left = 0,right = 1000000.0, m = (left+right)/2,ans = 1000000.0;

	while(right-left > EPS){
		if(check(m)){
			ans  = m;
			right = m-EPS;
		}else{
			left = m+EPS;
		}
		m = (left+right)/2;
	}

	printf("%.10lf\n",ans);
}

int main(){

	while(true){
		scanf("%d %lf",&N,&L);
		if(N == 0)break;

		func();
	}

	return 0;
}

