  while(cin>>n>>L&&n){
    vector<Info> in;
    int s,t,c;
    rep(i,n){
      cin>>s>>t>>c;
      in.push_back(Info(s,t,c));
    }
    printf("%.8lf\n",solve(in,L));
  }
}

