#define lt(a,b) (a-b < -EPS)
#define le(a,b) (lt(a,b) || equal(a,b))
#define MAX_N 86401
#define MAX_L 1000000
#define END 86400

struct P{
  double s,t,u;
  P(){}
  P(double s,double t,double u) : s(s),t(t),u(u) {}
  bool operator < (const P &p)const{
    return s < p.s;
  }
};

