      for(int j = 0 ; j < 2 ; j++){
        double prev = 0;
        for(int k = 0 ; k < N ; k++){
          rem[j] = min(L,rem[j]+mid*(p[k].s-prev));
          double d = p[k].t-p[k].s;
          rem[j] = min(L,rem[j]+d*(mid-p[k].u));
