  sort(in.begin(),in.end());
  double mini=0,maxi=1000000,ret=0,mid;
  while(mini+eps<maxi){
    double mid = (mini+maxi)/2;
    if ( can_water(in,L,mid))ret=mid,maxi=mid-eps;
    else mini=mid+eps;
  }
