const double eps = 1e-7;
class Info{
public:
  int s,e,c,time;
  double consume;
  Info(){};
  Info(int ts,int te,int tc):s(ts),e(te),c(tc){
    consume=e-s;
    consume*=(double)c;
    time=e-s;
  };
};

double can_water(vector<Info >&in,double L,double p,double ini){
  double vol=ini;
  int lasttime=0;//last time that scheduled
  rep(i,in.size()){
    vol=min(L,vol+(double)(in[i].s-lasttime)*p);
    vol=vol+in[i].time*p-in[i].consume;
    if ( vol<=eps)return -1;
    vol=min(vol,L);
    lasttime=in[i].e;
  }

  vol=vol+(86400-lasttime)*p;
  vol=min(vol,L);
  return vol;
}

double solve(vector<Info > &in,double L){
  double mini=0,maxi=100000000,ret=0,mid;
  while(mini+eps<maxi){
