const double eps = 1e-7;
class Info{
public:
  int s,e,c,time;
  double consume;
  Info(){};
  Info(int ts,int te,int tc):s(ts),e(te),c(tc){
    consume=e-s;
    consume*=(double)c;
    time=e-s;
  };
};

double can_water(vector<Info >&in,double L,double p,double ini){
  double vol=ini;
  int lasttime=0;//last time that scheduled
  rep(i,in.size()){
    vol=min(L,vol+(double)(in[i].s-lasttime)*p);
    vol=vol+in[i].time*p-in[i].consume;
    if ( vol<=eps)return -1;
    vol=min(vol,L);
    lasttime=in[i].e;
  }

  vol=vol+(86400-lasttime)*p;
  vol=min(vol,L);
  return vol;
}

double solve(vector<Info > &in,double L){
  double mini=0,maxi=100000000000l,ret=0,mid;
  while(mini+eps<maxi){
    double mid = (mini+maxi)/2;
    double day1=can_water(in,L,mid,L);
    if ( day1<=eps){mini=mid+eps;continue;}

    double day2 = can_water(in,L,mid,day1);
    //printf("%.6lf %.6lf %.6lf\n",mid,day1,day2);
    if (day2>eps && day2-day1>=0)maxi=mid-eps,ret=mid;
    else mini=mid+eps;
  }
  return ret;
}

