bool C2(double v, double start) {
  double volume = start;
  double before = 0;
  rep(i, n) {
    volume += (s[i] - before) * v;
    if(volume > l) volume = l;

    double d = v - u[i];
    volume += d * (t[i] - s[i]);
    if(volume > l) volume = l;
    else if(volume < 0) return false;
    before = t[i];
  }
  volume += (86400-before) * v;
  return volume >= start;
}

bool C(double v) {
  double lower = 0;
  double upper = l;
  double mid;
  while(upper - lower > EPS) {
    mid = (lower + upper) / 2.0;
    if(C2(v, mid)) lower = mid;
    upper = mid;
  }
  return lower > EPS * 2.0;
}

