int n; long double l, u[86400];
bool solve(long double q) {
	long double T = l;
	for (int i = 0; i < 86400; i++) {
		T -= u[i % 86400]; T += q; if (T > l)T = l;
		if (T < 0)return false;
	}
	long double U = T;
	for (int i = 0; i < 86400; i++) {
		T -= u[i % 86400]; T += q; if (T > l)T = l;
		if (T < 0)return false;
	}
	if (T < U - 0.000001)return false;
	return true;
}
