void solve(int n,int l){
    vector<int> s(n),t(n),u(n);
    rep(i,0,n) cin >> s[i] >> t[i] >> u[i];
    if(t.back()<86400){
        s.push_back(t.back());
        t.push_back(86400);
        u.push_back(0);
    }
    if(s.front()>0){
        s.push_back(86400);
        t.push_back(86400+t.front()-s.front());
        u.push_back(0);
        int k=s.front();
        rep(i,0,s.size()){
            s[i]-=k;
            t[i]-=k;
        }
    }

    long double ans=0,mini=inf;
    int64_t p=0;
    rep(i,0,s.size()){
        p+=(t[i]-s[i])*u[i];
        mini=min(mini,u[i]-(long double)(l)/(t[i]-s[i]));
        auto ok=[&](long double x){
            if(i<s.size()-1) return x>=((long double)(p)-l)/t[i] and x>=mini;
            long double k=l;
            rep(j,0,s.size()){
                k+=(t[i]-s[i])*(x-u[i]);
                k=min(k,(long double)(l));
                if(k<0) return false;
            }
            return abs(k-l)<eps;
        };
        long double lb=ans,ub=inf;
        rep(j,0,64){
            long double mid=(lb+ub)/2;
            if(ok(mid)) ub=mid;
            else lb=mid;
        }
        ans=lb;
    }
    cout << ans << endl;
}

int main(){
    std::cin.tie(0);
    std::ios::sync_with_stdio(false);
    cout.setf(ios::fixed);
    cout.precision(10);
    for(;;){
        int n,l;
        cin >> n >> l;
        if(!n and !l) break;
        solve(n,l);
    }
    return 0;
}

