bool solve(void){
	int n,L,i,j;cin>>n>>L;
	if(n==0){return false;}
	//二分探索
	//2回やって大丈夫ならok
	vector<tuple<int,int,double>>use(n+n);
	double alluse=0;
	for(i=0;i<n;i++){
		int s,t;double u;cin>>s>>t>>u;
		use[i]=mt(s,t,u);
		use[i+n]=mt(s+86400,t+86400,u);
		alluse+=u*(t-s);
	}
	double bmin=alluse/86400,bmax=1e6;
	for(int kai=0;kai<65;kai++){
		double bgen;
		if(kai<20){bgen=sqrt((bmin+1)*(bmax+1))-1;}
		else{bgen=(bmin+bmax)/2;}

		double mae=0,tank=L;
		bool can=true;
		for(i=0;i<n+n;i++){
			int s,t;double u;
			tie(s,t,u)=use[i];
			tank+=bgen*(s-mae);
			mineq(tank,L);
			tank+=(bgen-u)*(t-s);
			mineq(tank,L);
			mae=t;
			if(tank<0){can=false;break;}
		}
		if(can){bmax=bgen;}
		else{bmin=bgen;}
	}
	cout<<bmax<<endl;
	return true;
}
