using namespace std;

#define DAY 86400
#define EPS 1e-8

int u[DAY], N, ft,L;

double calc()
{
	double f,hi=1e6, lo=0,pv=0,d1;
	for(;;)
	{
		f=(hi+lo)/2;
		bool g=true;
		double v=L;
		for(int i=0; i<DAY*2; i++)
		{
			v+=(f-1.0*u[i%DAY]);

			if(v>=L) v=L;
			if(v < 0)
			{
				g=false;
				lo=f;
				break;
			}
			if(i==DAY-1) d1=v;
		}

		if(g&&fabs(d1-v)<EPS)
		{
			if(fabs(f-pv) < EPS) break;
			pv=f;
			hi=f;
		}
		else lo=f;
	}

	return f;
}

int main()
{
	while(scanf("%d%d", &N, &L), (N||L))
	{
		memset(u,0,sizeof(u));
		for(int i=0; i<N; i++)
		{
			int s,t,f;
			scanf("%d%d%d", &s,&t,&f);
			for(int j=s; j<t; j++)
				u[j]=f;
		}
		printf("%.8lf\n", calc());
	}
}

