bool check(double f){
	double lf=l;
	double rec=0;
	bool flag=false;
	for(int j=0;j<3;j++){
		for(int i=0;i<=86400;i++){
			if(j==1&&a[i]!=0&&!flag){
				rec=lf;
				flag=true;
			}
			else if(j==2&&a[i]!=0){
				if(EQ(rec,lf))return true;
				return false;
			}
			double b=-a[i]+f;
			lf+=b;
			if(EQ(lf,0)||lf<0)return false;
			lf=min(lf,1.0*l);
		}
	}
	return true;
}

