			for(int i=s;i<=t;i++)a[i]=u;
		}
		double ub=1000000;
		double lb=0;
		int cnt=100;
		while(cnt--){
			double mid=(ub+lb)/2;
			if(check(mid))ub=mid;
			else lb=mid;
		}
		printf("%.10f\n",ub);
	}
	return 0;
}

