typedef std::pair<long, long> P;
P schedule[86405];
long u[86405];

bool pos(double cap){
	double cont = l;
	for(int j = 0; j < 2; j++){
		cont += cap * schedule[0].first;
		if(j == 1 && cont >= l){
			//printf("  possible_ret_1 cont = %lf, cap = %.10lf\n", cont, cap);
			return true;
		}
		cont = std::min(cont, (double)l);
		for(int i = 0; i < n; i++){
			//use
			cont += (cap - u[i]) * (schedule[i].second - schedule[i].first);
			if(cont < 0){
				return false;
			}

			//non_use
			if(i < n-1){
				cont += cap * (schedule[i+1].first - schedule[i].second);
			}else{
				cont += cap * (86400 - schedule[i].second);
			}
			if(j == 1 && cont >= l){
				//printf("  possible_ret_2 cont = %lf, cap = %.10lf\n", cont, cap);
				return true;
			}
			cont = std::min(cont, (double)l);
		}
	}
	return false;
}

int main(){
	while(1){
		scanf("%ld%ld", &n, &l);
		if(n == 0){
			return 0;
		}
		for(int i = 0; i < n; i++){
			scanf("%ld%ld%ld", &schedule[i].first, &schedule[i].second, u+i);
		}
		double ok = 1000000;
		double ng = 0;
		while(std::abs(ok - ng) > 0.0000001){
			double mid = (ok + ng) / 2;
			//printf("cap = %lf\n", mid);
			if(pos(mid)){
				ok = mid;
			}else{
				ng = mid;
			}
		}
		printf("%.10lf\n", ok);
	}
}

