int n,l;
int s[86402],t[86402],u[86402];

bool C(long double v){
	long double st=(long double)l;
	int now=s[0];
	int koko=0;
	for(int i=0;i<86400;i++){
		if(koko<n && t[koko]<=now)koko++;
		if(koko<n && s[koko]<=now){
			st-=(long double)u[koko];
		}
		st+=v;
		if(st<0.0)return false;
		st=min(st,(long double)l);
		now=(now+1)%86400;
	}
	return ((long double)st-l>1e-8);
}

int main(void){
	while(1){
		scanf("%d%d",&n,&l);
		if(n==0 && l==0)break;
